import{_ as t,r as o,o as r,c as l,a as e,b as s,w as a,F as c,d as i,e as d}from"./app.be1e3df6.js";var m="/THR81/assets/20220509110908.c749abb8.png",p="/THR81/assets/20220509111058.91048cf8.png",f="/THR81/assets/20220509111242.4685302e.png",h="/THR81/assets/20220509111331.ac319bbf.png";const u={},g=e("h1",{id:"mdf\u6982\u89C8",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#mdf\u6982\u89C8","aria-hidden":"true"},"#"),i(" MDF\u6982\u89C8")],-1),M={class:"table-of-contents"},F=i("MDF \u7EC4\u4EF6 MDF Components"),b=i("\u542F\u7528 MDF \u548C\u6388\u4E88\u6743\u9650 Enabling MDF and Granting Permissions"),D=i("\u542F\u7528MDF Enabling MDF"),_=i("\u6388\u4E88\u6743\u9650 Grant Permissions"),v=i("\u{1F389}\u7EC3\u4E60\u4E00 \u542F\u7528MDF\u548CMDF\u6743\u9650 Enabling MDF and MDF Permissions"),P=d('<div class="custom-container tip"><p class="custom-container-title">\u672C\u8282\u76EE\u6807</p><ul><li>\u6FC0\u6D3B\u5E76\u6388\u6743MDF</li></ul></div><p>\u4EC0\u4E48\u662F\u5143\u6570\u636E\u6846\u67B6(Metadata Framework)\uFF1F</p><p><img src="'+m+'" alt="Maximum Number of Custom MDF Objects"></p><p>\u5143\u6570\u636E\u6846\u67B6(Meta Data Framework,MDF) \u4E5F\u79F0\u4E3A\u901A\u7528\u5BF9\u8C61(Generic Object,GO)\uFF0C\u662F\u5141\u8BB8\u6269\u5C55\u73B0\u6709SuccessFactors HXM \u5957\u4EF6\u529F\u80FD\u7684\u5E73\u53F0\u529F\u80FD\u3002MDF\u80FD\u591F\u6DFB\u52A0\u65B0\u7684\u81EA\u5B9A\u4E49\u5BF9\u8C61\u5E76\u7EF4\u62A4\u540D\u4E3A\u901A\u7528\u5BF9\u8C61\u7684\u73B0\u6709\u5BF9\u8C61\u3002</p><p>\u4F7F\u7528MDF\uFF0C\u60A8\u53EF\u4EE5\u521B\u5EFA\u548C\u7BA1\u7406\u5BF9\u8C61\u3001\u94FE\u63A5\u5BF9\u8C61\uFF0C\u751A\u81F3\u521B\u5EFA\u5BF9\u8C61\u5C42\u6B21\u3002MDF \u4E0E\u5DE5\u4F5C\u6D41\u3001\u89C4\u5219\u5F15\u64CE\u548C\u62A5\u8868\u5B8C\u7F8E\u96C6\u6210\u3002\u5B83\u8FD8\u5177\u6709\u5185\u7F6E\u529F\u80FD\uFF0C\u5982\u5BFC\u5165\u548C\u5BFC\u51FA\u652F\u6301\u4EE5\u53CA\u57FA\u4E8E\u89D2\u8272\u7684\u6743\u9650(RBP) \u3002\u8FD9\u610F\u5473\u7740 MDF\u4E2D\u7684\u4EFB\u4F55\u53EF\u7528\u5BF9\u8C61\u90FD\u5177\u6709\u6240\u6709\u8FD9\u4E9B\u529F\u80FD\u3002\u7136\u540E\uFF0C\u60A8\u53EF\u4EE5\u4F7F\u7528\u4E0D\u540C\u7684\u65B9\u5F0F\u4EE5\u5BFC\u5165/\u5BFC\u51FA\u3001UI \u6216 oData\u7B49\u65B9\u5F0F\u7BA1\u7406\u6570\u636E\uFF0C\u5E76\u4E14\u60A8\u53EF\u4EE5\u63A7\u5236\u8BBF\u95EE\u8FD9\u4E9B\u5BF9\u8C61\u7684\u4EBA\u5458\u3002</p><p>\u4F7F\u7528MDF\uFF0C\u60A8\u53EF\u4EE5\u8F7B\u677E\u6269\u5C55\u5BA2\u6237\u7684 Employee Central (EC) \u5B9E\u4F53\u3002\u5B83\u5141\u8BB8\u60A8\u5B9A\u5236\u6EE1\u8DB3\u5BA2\u6237\u9700\u6C42\u6240\u9700\u7684\u5BF9\u8C61\u548C\u884C\u4E3A\u3002\u4F7F\u7528MDF\u529F\u80FD\u6784\u5EFASAP SuccessFactors \u5957\u4EF6\u7684\u51E0\u4E2A\u5173\u952E\u5E94\u7528\u7A0B\u5E8F\uFF0C\u5982\u804C\u4F4D\u7BA1\u7406(Position Management,)\u3001\u4F11\u5047(Time Off)\u3001\u7BA1\u7406\u4E1A\u52A1\u914D\u7F6E(Manage Business Configuration,BCUI) \u3002</p><p>\u7BA1\u7406\u4E2D\u5FC3\u63D0\u4F9B\u4E86\u7528\u4E8E\u7BA1\u7406MDF\u5BF9\u8C61\u7684\u5DE5\u5177\u3002</p><p>\u7BA1\u7406\u5458\u9ED8\u8BA4\u80FD\u591F\u521B\u5EFA 250 \u4E2A\u81EA\u5B9A\u4E49MDF\u5BF9\u8C61\u3002\u53EF\u901A\u8FC7Provisioning &gt; Company Settings\u5C06\u6B64\u9650\u989D\u63D0\u9AD8\u5230 400 \u3002</p><h2 id="mdf-\u7EC4\u4EF6-mdf-components" tabindex="-1"><a class="header-anchor" href="#mdf-\u7EC4\u4EF6-mdf-components" aria-hidden="true">#</a> MDF \u7EC4\u4EF6 MDF Components</h2><p>MDF\u53EF\u4EE5\u62E5\u6709\u591A\u4E2A\u4E0D\u540C\u7684\u5C42(layers)\u3002\u6700\u57FA\u672C\u7684\u5C42\u662F\u5BF9\u8C61\u5B9A\u4E49(Object Definition)\u3002\u5728\u6B64\u5904\u5236\u5B9A\u5BF9\u8C61\u7684\u6240\u6709\u914D\u7F6E\u51B3\u7B56\u4EE5\u53CA\u5BF9\u8C61\u7684\u4F4D\u7F6E\u53EB\u201C\u6784\u5EFA(build)\u201D\u3002\u5BF9\u8C61\u884C\u4E3A(Object behavior)\u3001\u5B57\u6BB5(fields)\u3001\u5173\u8054(associations)\u548C\u5B89\u5168\u6027(security )\u5747\u5728\u5BF9\u8C61\u5B9A\u4E49\u4E2D\u8BBE\u7F6E\u3002</p><p>\u6784\u5EFA\u5BF9\u8C61\u5B9A\u4E49\u540E\uFF0C\u53EF\u4EE5\u5C06\u4E1A\u52A1\u903B\u8F91\u6DFB\u52A0\u5230\u5BF9\u8C61\uFF0C\u4F7F\u7528\u4E1A\u52A1\u89C4\u5219(Business Rules)\u5B8C\u6210\u6B64\u64CD\u4F5C\u3002\u4E1A\u52A1\u89C4\u5219\u53EF\u7528\u4E8E\u8BBE\u7F6E\u9ED8\u8BA4\u6570\u636E\uFF0C\u6839\u636E\u9009\u62E9\u7684\u6570\u636E\u8BBE\u7F6E\u503C\uFF0C\u5F15\u53D1\u4FE1\u606F\u6216\u9519\u8BEF\u6D88\u606F\uFF0C\u5E76\u5728\u4FDD\u5B58\u6216\u5220\u9664\u6570\u636E\u8BB0\u5F55\u65F6\u89E6\u53D1\u64CD\u4F5C\u3002</p><p>\u6784\u5EFA\u5BF9\u8C61\u5E76\u96C6\u6210\u4E1A\u52A1\u903B\u8F91\u540E\uFF0C\u60A8\u53EF\u4EE5\u5F00\u59CB\u5BFC\u5165\u3001\u521B\u5EFA\u548C\u7BA1\u7406\u4E0E\u8BE5\u5BF9\u8C61\u76F8\u5173\u7684\u6570\u636E\u3002</p><p><img src="'+p+`" alt="Object Views"></p><p>\u6700\u540E\uFF0C\u60A8\u53EF\u4EE5\u4E3A\u5BF9\u8C61\u521B\u5EFA\u4E0D\u540C\u7684\u201C\u89C6\u56FE(views)\u201D\u6216 UI\u3002\u5728\u914D\u7F6EUI\u4E2D\uFF0C\u60A8\u53EF\u4EE5\u66F4\u6539\u5BF9\u8C61\u7684\u5E03\u5C40\u3001\u6DFB\u52A0\u6216\u79FB\u9664\u5B57\u6BB5\u3001\u66F4\u65B0\u6807\u7B7E\u4EE5\u53CA\u5C06\u6587\u672C\u6216\u8D85\u94FE\u63A5\u6DFB\u52A0\u5230\u5BF9\u8C61\u89C6\u56FE\u3002</p><h2 id="\u542F\u7528-mdf-\u548C\u6388\u4E88\u6743\u9650-enabling-mdf-and-granting-permissions" tabindex="-1"><a class="header-anchor" href="#\u542F\u7528-mdf-\u548C\u6388\u4E88\u6743\u9650-enabling-mdf-and-granting-permissions" aria-hidden="true">#</a> \u542F\u7528 MDF \u548C\u6388\u4E88\u6743\u9650 Enabling MDF and Granting Permissions</h2><h3 id="\u542F\u7528mdf-enabling-mdf" tabindex="-1"><a class="header-anchor" href="#\u542F\u7528mdf-enabling-mdf" aria-hidden="true">#</a> \u542F\u7528MDF Enabling MDF</h3><p>\u8981\u542F\u7528MDF\uFF0C\u8BF7\u8F6C\u5230Provisioning \u2014&gt; Company Settings</p><pre><code>- \u9009\u4E2D\u542F\u7528\u901A\u7528\u5BF9\u8C61\u590D\u9009\u6846\u3002  
Select the Enable Generic Objects checkbox.

- \u9009\u4E2D\u542F\u7528\u9644\u4EF6\u7BA1\u7406\u5668\u590D\u9009\u6846\u3002  
Select the Enable the Attachment Manager checkbox. Scroll up to the top of the screen and select Save Feature.
</code></pre><p>\u5411\u4E0A\u6EDA\u52A8\u5230\u5C4F\u5E55\u9876\u90E8\u5E76\u9009\u62E9\u4FDD\u5B58\u529F\u80FD(Save Feature)\u3002</p><p>\u5982\u679C\u8981\u4F7F\u7528\u5176\u4ED6\u76F8\u5173\u529F\u80FD\uFF08\u4F8B\u5982\u5DE5\u4F5C\u6D41\u548C\u89C4\u5219\uFF09\uFF0C\u8FD8\u5FC5\u987B\u4ECEProvisioning\u4E2D\u542F\u7528\u4EE5\u4E0B\u529F\u80FD\u3002</p><pre><code>- Employee Central\u57FA\u7840\u5BF9\u8C61  
Employee Central Foundation Objects

- \u57FA\u4E8E\u751F\u6548\u65E5\u671F\u7684\u6570\u636E\u5E73\u53F0  
Effective Dated Data Platform

- \u542F\u7528\u5DE5\u4F5C\u6D41\u6D3E\u751F\u7684\u4E1A\u52A1\u89C4\u5219  
Enable Business Rules for Workflow Derivation
</code></pre><div class="custom-container warning"><p class="custom-container-title">Note</p><p>\u8981\u542F\u7528MDF\u4EE5\u53CA\u6269\u5C55\u4E2D\u5FC3(Extensions Center)\uFF0C\u53EF\u4EE5\u4F7F\u7528\u5347\u7EA7\u4E2D\u5FC3\u3002</p></div><h3 id="\u6388\u4E88\u6743\u9650-grant-permissions" tabindex="-1"><a class="header-anchor" href="#\u6388\u4E88\u6743\u9650-grant-permissions" aria-hidden="true">#</a> \u6388\u4E88\u6743\u9650 Grant Permissions</h3><p>\u5BF9\u7BA1\u7406MDF\u914D\u7F6E\u548C\u6570\u636E\u7684\u5DE5\u5177\u7684\u8BBF\u95EE\u7531(RBP) \u63A7\u5236\u3002\u542F\u7528MDF\u540E\uFF0C\u60A8\u9700\u8981\u901A\u8FC7\u6388\u4E88\u7279\u5B9ARBP\u6743\u9650\u4EE5\u6388\u4E88\u8BBF\u95EE\u6743\u9650\u3002</p><ol><li><p>\u4F7F\u7528Action Search\u5BFC\u822A\u5230Manage Permission Roles\u3002</p></li><li><p>\u9009\u62E9\u73B0\u6709\u89D2\u8272\u6216\u521B\u5EFA\u65B0\u7684Permission Role\u3002</p></li><li><p>\u5355\u51FB\u201C\u6743\u9650(Permission)\u201D</p></li><li><p><img src="`+f+'" alt="Metadata Framework Permissions"></p><p>\u5BFC\u822A\u5230\u5143\u6570\u636E\u6846\u67B6(Metadata Framework)\u90E8\u5206\uFF0C\u5E76\u9009\u62E9\u60A8\u5E0C\u671B\u6388\u4E88\u8BE5\u89D2\u8272\u7684\u6240\u6709\u6743\u9650\u3002\u6700\u521D\uFF0C\u5E94\u5C06\u6240\u6709\u8FD9\u4E9B\u6743\u9650\u6388\u4E88\u7BA1\u7406\u5458\u89D2\u8272\uFF0C\u5E76\u4E14\u5728\u719F\u6089\u4E3B\u9898\u540E\uFF0C\u53EF\u4EE5\u51B3\u5B9A\u4FDD\u7559\u6216\u64A4\u6D88\u8FD9\u4E9B\u6743\u9650\u3002</p><div class="custom-container warning"><p class="custom-container-title">Note</p><p>\u8FD8\u6709\u5176\u4ED6RBP \u90E8\u5206\u5141\u8BB8\u7528\u6237\u5904\u7406\u4F7F\u7528MDF\u6784\u5EFA\u7684\u5BF9\u8C61\uFF08\u4F8B\u5982\uFF0C\u6742\u9879\u6743\u9650(Miscellaneous Permissions)\u6216\u7BA1\u7406\u4F11\u5047(Manage Time Off)\uFF09\u3002\u5982\u679C\u672A\u4ECEProvisioning\u4E2D\u542F\u7528\u76F8\u5173\u529F\u80FD\uFF0C\u5219\u67D0\u4E9B\u6743\u9650\uFF08\u5982\u201C\u7BA1\u7406\u804C\u4F4D(Manage Position)\u201D\uFF09\u53EF\u80FD\u4E0D\u53EF\u7528\u3002</p></div></li><li><p>\u5728\u6742\u9879\u6743\u9650(Miscellaneous Permissions)\u90E8\u5206\uFF0C\u60A8\u53EF\u4EE5\u6388\u4E88\u5BF9\u7BA1\u7406\u6570\u636E\u7684\u5DE5\u5177\u7684\u8BBF\u95EE\u6743\u9650\uFF0C\u4EE5\u53CA\u67E5\u770B\u3001\u7F16\u8F91\u3001\u5BFC\u5165\u548C\u5BFC\u51FA\u7279\u5B9AMDF\u5BF9\u8C61\u7684\u6570\u636E\u7684\u529F\u80FD\u3002\u8FD8\u5B58\u5728\u201C\u5B57\u6BB5\u7EA7\u522B\u8986\u76D6(Field Level Overrides)\u201D\u9009\u9879\uFF0C\u7528\u4E8E\u5BF9\u5BF9\u8C61\u7684\u7279\u5B9A\u5B57\u6BB5\u5B9A\u4E49\u66F4\u4E25\u683C\u7684\u6743\u9650\uFF08\u65E0\u6CD5\u8BBF\u95EE\u6216\u53EA\u8BFB\uFF09\u3002</p><p><img src="'+h+'" alt="Miscellaneous Permissions"></p></li></ol><h3 id="\u7EC3\u4E60\u4E00-\u542F\u7528mdf\u548Cmdf\u6743\u9650-enabling-mdf-and-mdf-permissions" tabindex="-1"><a class="header-anchor" href="#\u7EC3\u4E60\u4E00-\u542F\u7528mdf\u548Cmdf\u6743\u9650-enabling-mdf-and-mdf-permissions" aria-hidden="true">#</a> \u{1F389}\u7EC3\u4E60\u4E00 \u542F\u7528MDF\u548CMDF\u6743\u9650 Enabling MDF and MDF Permissions</h3><p>Your company has decided to start using MDF for configuration of their objects. Your task is to enable MDF and to give permissions to Administrators to use it.</p><ol><li><p>Enable MDF in provisioning.</p><ol><li>In Provisioning, select Company Settings.</li><li>Verify that the checkboxes Enable Generic Objects and Enable the Attachment Manager are both enabled.</li><li>If you make a change click Save Feature, type your Company ID and click Confirm .</li></ol></li><li><p>Set up permissions for administrators to use MDF.</p><ol><li>Use the Action Search to navigate to Manage Permission Roles.</li><li>In the list of permission roles, click Administrators.</li><li>On the Permission Role Detail screen, click Permission.</li><li>On the left side, click Metadata Framework.</li><li>Make sure all boxes are marked/checked.</li><li>Click Done.</li><li>Click save changes</li></ol></li></ol><p>MDF is enabled in your instance and your administrators has the permission to Configure Object Definition. As usual, after changing the permissions, it is important to log out and come back to the instance to make sure that the new permissions will be reflected.</p>',29);function k(C,E){const n=o("RouterLink");return r(),l(c,null,[g,e("nav",M,[e("ul",null,[e("li",null,[s(n,{to:"#mdf-\u7EC4\u4EF6-mdf-components"},{default:a(()=>[F]),_:1})]),e("li",null,[s(n,{to:"#\u542F\u7528-mdf-\u548C\u6388\u4E88\u6743\u9650-enabling-mdf-and-granting-permissions"},{default:a(()=>[b]),_:1}),e("ul",null,[e("li",null,[s(n,{to:"#\u542F\u7528mdf-enabling-mdf"},{default:a(()=>[D]),_:1})]),e("li",null,[s(n,{to:"#\u6388\u4E88\u6743\u9650-grant-permissions"},{default:a(()=>[_]),_:1})]),e("li",null,[s(n,{to:"#\u7EC3\u4E60\u4E00-\u542F\u7528mdf\u548Cmdf\u6743\u9650-enabling-mdf-and-mdf-permissions"},{default:a(()=>[v]),_:1})])])])])]),P],64)}var y=t(u,[["render",k],["__file","Lesson1.html.vue"]]);export{y as default};
