import{_ as o,r as n,o as a,c as l,a as e,b as t,w as s,F as p,d as r,e as c}from"./app.be1e3df6.js";var h="/THR81/assets/20220509124008.268b3b8e.png",d="/THR81/assets/20220509124049.892764a7.png",u="/THR81/assets/20220509124132.72f294ce.png",m="/THR81/assets/20220509124212.97693247.png",g="/THR81/assets/20220509124342.55e06841.png",b="/THR81/assets/20220509124417.fef4b235.png",R="/THR81/assets/20220509124506.3c889384.png";const C={},_=e("h1",{id:"\u62A5\u8868\u4E2D\u5FC3\u7B80\u4ECB-\u6240\u6709\u5176\u4ED6\u62A5\u8868\u5DE5\u5177\u7684\u7F51\u5173",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u62A5\u8868\u4E2D\u5FC3\u7B80\u4ECB-\u6240\u6709\u5176\u4ED6\u62A5\u8868\u5DE5\u5177\u7684\u7F51\u5173","aria-hidden":"true"},"#"),r(" \u62A5\u8868\u4E2D\u5FC3\u7B80\u4ECB-\u6240\u6709\u5176\u4ED6\u62A5\u8868\u5DE5\u5177\u7684\u7F51\u5173")],-1),P={class:"table-of-contents"},v=r("\u62A5\u8868\u4E2D\u5FC3 Report Center"),f=r("\u62A5\u8868\u4E2D\u5FC3-\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177 Report Center - Configuration Check Tool"),S=r("\u4E3A\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u5206\u914D\u7BA1\u7406\u6743\u9650 Assigning Administration Permissions to Manage the Report Center"),y=r("\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3 Manage Report Center"),A=r("\u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u672A\u8FD4\u56DE\u6240\u6709\u8D39\u7528\u62A5\u8868 Report Center Search not Returning all Reports"),w=r("\u5C06\u7528\u6237\u8BBF\u95EE\u6743\u9650\u5206\u914D\u5230\u62A5\u8868\u4E2D\u5FC3 Assigning User Access to the Report Center"),k=r("\u4E86\u89E3\u62A5\u8868\u4E2D\u5FC3\u6743\u9650 Understanding the Report Center Permission"),T=r("\u5206\u914D\u9644\u52A0\u7528\u6237\u6743\u9650 Assigning Additional User Permissions"),x=r("\u753B\u5E03\u62A5\u8868\u6743\u9650 Canvas Report Permissions"),U=r("\u5206\u6790\u529F\u80FD\u677F\u5757\u548C\u4FE1\u606F\u9762\u677F\u6743\u9650 Analytics Tiles and Dashboards Permission"),D=r("\u{1F389}\u7EC3\u4E60\u4E00 \u62A5\u8868\u4E2D\u5FC3\u6743\u9650 Report Center Permission"),M=c('<div class="custom-container tip"><p class="custom-container-title">\u672C\u8282\u76EE\u6807</p><ul><li>\u6743\u9650\u62A5\u8868\u4E2D\u5FC3\u7BA1\u7406\u5458\u548C\u7528\u6237</li></ul></div><h2 id="\u62A5\u8868\u4E2D\u5FC3-report-center" tabindex="-1"><a class="header-anchor" href="#\u62A5\u8868\u4E2D\u5FC3-report-center" aria-hidden="true">#</a> \u62A5\u8868\u4E2D\u5FC3 Report Center</h2><p><img src="'+h+'" alt="Report Center"></p><p>\u62A5\u8868\u4E2D\u5FC3\u96C6\u4E2D\u4E86\u6765\u81EASAP SuccessFactors \u4E2D\u4E0D\u540C\u5206\u6790\u5DE5\u5177\u7684\u6240\u6709\u62A5\u8868\u7EC4\u4EF6\u3002\u8FD9\u4F7F\u7BA1\u7406\u5458\u548C\u7528\u6237\u53EF\u4EE5\u5728\u4E2D\u592E\u4F4D\u7F6E\u7F16\u8F91\u548C\u8FD0\u884C\u62A5\u8868\u3002\u5B83\u57FA\u4E8ESAP Fiori \u754C\u9762\u539F\u5219\u8BBE\u8BA1\u3002</p><h3 id="\u62A5\u8868\u4E2D\u5FC3-\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-report-center-configuration-check-tool" tabindex="-1"><a class="header-anchor" href="#\u62A5\u8868\u4E2D\u5FC3-\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-report-center-configuration-check-tool" aria-hidden="true">#</a> \u62A5\u8868\u4E2D\u5FC3-\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177 Report Center - Configuration Check Tool</h3><p>\u62A5\u8868\u4E2D\u5FC3\u914D\u7F6E\u68C0\u67E5\u786E\u5B9A\u662F\u5426\uFF1A</p><ul><li><p>\u62A5\u8868\u4E2D\u5FC3\u5DF2\u542F\u7528<br> Report Center is enabled</p></li><li><p>\u201C\u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u201D\u5DF2\u542F\u7528<br> Report Center Search is enabled</p></li><li><p>\u516C\u53F8\u5206\u914D\u5230Solr \u7FA4\u96C6<br> Company is assigned to a Solr cluster</p></li><li><p>\u62A5\u8868\u4E2D\u5FC3\u6B63\u5728\u4F7F\u7528Solr \u8FDB\u884C\u5217\u8868<br> Report Center is using Solr for listing</p></li><li><p>\u516C\u53F8\u7684Solr \u96C6\u5408\u53EF\u8BBF\u95EE<br> Solr collection for the company is accessible</p></li></ul><h2 id="\u4E3A\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u5206\u914D\u7BA1\u7406\u6743\u9650-assigning-administration-permissions-to-manage-the-report-center" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u5206\u914D\u7BA1\u7406\u6743\u9650-assigning-administration-permissions-to-manage-the-report-center" aria-hidden="true">#</a> \u4E3A\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u5206\u914D\u7BA1\u7406\u6743\u9650 Assigning Administration Permissions to Manage the Report Center</h2><p><img src="'+d+'" alt="Administration Permissions to Manage the Report Center"></p><p>\u542F\u7528\u201C\u62A5\u8868\u201D\u4E2D\u5FC3\u540E\uFF0C\u60A8\u53EF\u4EE5\u5141\u8BB8\u7BA1\u7406\u5458\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u7684\u67D0\u4E9B\u65B9\u9762\u3002\u60A8\u53EF\u4EE5\u5728\u201C\u7BA1\u7406\u4E2D\u5FC3\u201D\u4E2D\u5411\u201C\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u201D\u5DE5\u5177\u5206\u914D\u6743\u9650\uFF1A</p><ol><li><p>\u8F6C\u5230\u201C\u7BA1\u7406\u5458\u4E2D\u5FC3\u201D&gt;\u201C\u7BA1\u7406\u89D2\u8272\u6743\u9650\u201D<br> Go to Admin Center &gt; Manage Role Permissions</p></li><li><p>\u9009\u62E9\u9002\u5F53\u7684\u89D2\u8272<br> Select the appropriate role</p></li><li><p>\u9009\u62E9\u6743\u9650...<br> Choose Permission...</p></li><li><p>\u8F6C\u5230\u7BA1\u7406\u6743\u9650/ \u7BA1\u7406\u4EEA\u8868\u76D8/ \u62A5\u8868<br> Go to Administrative Permissions/Manage Dashboard/Reports</p></li><li><p>\u542F\u7528\u4EE5\u4E0B\u5185\u5BB9\uFF1A<br> Enable the following:</p><ul><li>\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3- \u8BBF\u95EE\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u5DE5\u5177\u3002<br> Manage Report Center- access Manage Report Center tool.</li><li>\u5411\u7EC4\u548C\u89D2\u8272\u5171\u4EAB\u62A5\u8868- \u4E0E\u7EC4\u548C\u89D2\u8272\u5171\u4EAB\u62A5\u8868\uFF0C\u800C\u4E0D\u4EC5\u4EC5\u662F\u5355\u4E2A\u7528\u6237\u3002<br> Share Reports to Groups &amp; Roles - share reports with goups and roles, not just individual users.</li><li>\u67E5\u770B\u6240\u6709\u8BA1\u5212- \u67E5\u770B\u6240\u6709\u7528\u6237\u62E5\u6709\u7684\u8BA1\u5212\u3002<br> View All Schedules - view schedules owned by all users</li><li>\u5C06\u62A5\u8868\u8BA1\u5212\u5230FTP \u76EE\u6807\uFF0D \u8BA1\u5212\u901A\u8FC7FTP \u4F20\u9001\u62A5\u8868\u3002<br> Schedule Reports to FTP Destination - schedule a repor to be delivered via FTP.</li></ul></li></ol><h3 id="\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3-manage-report-center" tabindex="-1"><a class="header-anchor" href="#\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3-manage-report-center" aria-hidden="true">#</a> \u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3 Manage Report Center</h3><p><img src="'+u+'" alt="Manage Report Center"></p><p>\u542F\u7528\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u5DE5\u5177\u540E\uFF0C\u7BA1\u7406\u5458\u53EF\u4EE5\uFF1A</p><ul><li><p>\u542F\u7528\u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u529F\u80FD\uFF1A\u542F\u7528\u62A5\u8868\u4E2D\u5FC3\u4E2D\u7684\u641C\u7D22\u6846\u3002<br> Enable Report Center Search Functionalit:yEnable search box in the Report Center.</p></li><li><p>\u4F7F\u7528SOLR \u52A0\u8F7D\u62A5\u8868\u4E2D\u5FC3\uFF1A\u4F7F\u7528SOLR \u6280\u672F\uFF08\u641C\u7D22\u5F15\u64CE\uFF09\u751F\u6210\u201C\u62A5\u8868\u4E2D\u5FC3\u201D\u5217\u8868\u9875\u3002<br> Use SOLR to load Report Cente:rUse SOLR technology (a search engine) to generate Report Center listing page.</p></li><li><p>\u663E\u793A\u5C42\u6B21\u7ED3\u6784\u4E2D\u7684\u6807\u7B7E\uFF1A\u5141\u8BB8\u60A8\u4FDD\u7559\u5728\u201C\u62A5\u8868\u4E2D\u5FC3\u201D\u7684\u201C\u6807\u7B7E\u201D\u89C6\u56FE\u4E0A\u5D4C\u5957\u6807\u7B7E\u7684\u5C42\u6B21\u7ED3\u6784\u663E\u793A\uFF0C\u5373\u4F7F\u5C42\u6B21\u7ED3\u6784\u4E2D\u7684\u6807\u7B7E\u4E0E\u62A5\u8868\u65E0\u5173\u3002\u542F\u7528\u540E\uFF0C\u60A8\u53EF\u4EE5\u5728\u6807\u7B7E\u89C6\u56FE\u4E2D\u5C55\u5F00\u6BCF\u4E2A\u6807\u7B7E\uFF0C\u4EE5\u67E5\u770B\u5173\u8054\u7684\u62A5\u8868\u548C/ \u6216\u5C42\u6B21\u7ED3\u6784\u4E2D\u7684\u4E0B\u4E00\u4E2A\u6807\u7B7E\u3002\u7981\u7528\u65F6\uFF0C\u60A8\u53EA\u80FD\u4F7F\u7528\u76F8\u5173\u62A5\u8868\u5C55\u5F00\u6807\u7B7E\u3002\u65E0\u62A5\u8868\u7684\u5D4C\u5957\u6807\u7B7E\u4E0E\u5C42\u6B21\u4E2D\u7684\u4E0B\u4E00\u4E2A\u6807\u7B7E\u5408\u5E76\uFF0C\u76F4\u5230\u4E0E\u62A5\u8868\u76F8\u5173\u8054\u7684\u6807\u7B7E\u5408\u5E76\uFF0C\u5E76\u663E\u793A\u4E3A\u5408\u5E76\u8DEF\u5F84\u3002<br> Show labels in a Hierarchical Structur:eAllows you to retain a hierarchical display of nested labels on the Labels view in Report Center, even when labels in the hierarchy aren\u2019t associated with reports. When enabled, you expand every label in the Labels view to see the associated reports and/or the next label in the hierarchy. When disabled, you expand only the labels with associated reports. The nested labels without reports merge with the next label in the hierarchy, up to the label that&#39;s associated with reports, and appear as merged paths.</p></li></ul><h3 id="\u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u672A\u8FD4\u56DE\u6240\u6709\u8D39\u7528\u62A5\u8868-report-center-search-not-returning-all-reports" tabindex="-1"><a class="header-anchor" href="#\u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u672A\u8FD4\u56DE\u6240\u6709\u8D39\u7528\u62A5\u8868-report-center-search-not-returning-all-reports" aria-hidden="true">#</a> \u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u672A\u8FD4\u56DE\u6240\u6709\u8D39\u7528\u62A5\u8868 Report Center Search not Returning all Reports</h3><p><img src="'+m+'" alt="Sync Tool for the Report Center Search"></p><p>\u4E0D\u540C\u7C7B\u578B\u62A5\u8868\u548C\u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u7684\u540C\u6B65\u5E94\u81EA\u52A8\u8FDB\u884C\u3002\u4F46\u662F\uFF0C\u5982\u679C\u641C\u7D22\u672A\u8FD4\u56DE\u9884\u671F\u7ED3\u679C\uFF0C\u7BA1\u7406\u5458\u53EF\u4EE5\u624B\u52A8\u5C06\u62A5\u8868\u4FE1\u606F\u540C\u6B65\u5230\u5DF2\u4F7F\u7528\u540C\u6B65\u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u5DE5\u5177\u8BBE\u7F6E\u7684\u641C\u7D22\u8D44\u6E90\u5E93\u4E2D\u3002</p><p>\u6B64\u5DE5\u5177\u5728Admin Center &gt; Reporting and Analytics &gt; Sync Report Center Search\u201D\u4E2D\u53EF\u7528\u3002</p><div class="custom-container warning"><p class="custom-container-title">Note</p><p>\u540C\u6B65\u53EF\u80FD\u9700\u8981\u4E00\u4E9B\u65F6\u95F4\uFF0C\u5982\u679C\u60A8\u6709\u4EFB\u4F55\u95EE\u9898\uFF0C\u8BF7\u8054\u7CFB\u652F\u6301\u90E8\u95E8\u4EE5\u68C0\u67E5\u540C\u6B65\u72B6\u6001\u3002</p></div><h3 id="\u5C06\u7528\u6237\u8BBF\u95EE\u6743\u9650\u5206\u914D\u5230\u62A5\u8868\u4E2D\u5FC3-assigning-user-access-to-the-report-center" tabindex="-1"><a class="header-anchor" href="#\u5C06\u7528\u6237\u8BBF\u95EE\u6743\u9650\u5206\u914D\u5230\u62A5\u8868\u4E2D\u5FC3-assigning-user-access-to-the-report-center" aria-hidden="true">#</a> \u5C06\u7528\u6237\u8BBF\u95EE\u6743\u9650\u5206\u914D\u5230\u62A5\u8868\u4E2D\u5FC3 Assigning User Access to the Report Center</h3><p>\u914D\u7F6E\u201C\u62A5\u8868\u4E2D\u5FC3\u201D\u7684\u6700\u540E\u4E00\u6B65\u662F\u786E\u5B9A\u8C01\u9700\u8981\u8BBF\u95EE\u62A5\u8868\u4E2D\u5FC3\u5DE5\u5177\u5E76\u5206\u914D\u6743\u9650\u3002\u5728\u7EBF\u521B\u5EFA\u6216\u67E5\u770B\u62A5\u8868\u7684\u7528\u6237\u9700\u8981\u8BBF\u95EE\u62A5\u8868\u4E2D\u5FC3\u3002 \u786E\u5B9A\u76F8\u5E94\u5458\u5DE5\u540E\uFF0C\u5C06\u76F8\u5E94\u7684RBP \u6743\u9650\u5206\u914D\u7ED9\u89D2\u8272\uFF1A</p><ol><li><p>\u8F6C\u5230\u7BA1\u7406\u5458\u4E2D\u5FC3\u2192 \u7BA1\u7406\u89D2\u8272\u6743\u9650<br> Go to Admin Center Manage Role Permissions</p></li><li><p>\u9009\u62E9\u9002\u5F53\u7684\u89D2\u8272<br> Select the appropriate role</p></li><li><p>\u9009\u62E9\u6743\u9650...<br> Choose Permission...</p></li><li><p>\u5728\u201C\u7528\u6237\u6743\u9650\u201D\u4E0B\uFF0C\u8F6C\u5230\u201C\u62A5\u8868\u6743\u9650\u201D<br> Under User Permissions, go to Reports Permission</p></li><li><p>\u542F\u7528\u76F8\u5E94\u7684\u6743\u9650\uFF1A<br> Enable the appropriate permissions:</p><ol><li><p>\u62A5\u8868\u4E2D\u5FC3\uFF1A\u542F\u7528\u5BF9\u62A5\u8868\u4E2D\u5FC3\u9875\u9762\u7684\u8BBF\u95EE\u3002<br> Report Center: enable access to the report center page.</p></li><li><p>\u8BA1\u5212\u62A5\u8868\uFF1A\u5141\u8BB8\u7528\u6237\u5728\u5176\u62A5\u8868\u4E0A\u521B\u5EFA\u548C\u7BA1\u7406\u8BA1\u5212\u3002<br> Schedule Reports: enable a user to create and manage schedules on their reports. Understanding the Report Center Permission</p></li></ol></li></ol><h3 id="\u4E86\u89E3\u62A5\u8868\u4E2D\u5FC3\u6743\u9650-understanding-the-report-center-permission" tabindex="-1"><a class="header-anchor" href="#\u4E86\u89E3\u62A5\u8868\u4E2D\u5FC3\u6743\u9650-understanding-the-report-center-permission" aria-hidden="true">#</a> \u4E86\u89E3\u62A5\u8868\u4E2D\u5FC3\u6743\u9650 Understanding the Report Center Permission</h3><p>\u62A5\u8868\u4E2D\u5FC3\u5C0A\u91CD\u62A5\u8868\u5DE5\u5177\u7684RBP \u6743\u9650\u3002</p><p>\u62A5\u8868\u4E2D\u5FC3\u5177\u6709\u4EE5\u4E0B\u5DE5\u5177\u7279\u5B9A\u8981\u6C42\uFF1A</p><ul><li><p>\u753B\u5E03\u62A5\u8868\uFF08\u4EE5\u524D\u6765\u81EA\u8054\u673A\u62A5\u8868\u8BBE\u8BA1\u5668\uFF09\uFF1A<br> Canvas Reports (previously from Online Report Designer):</p><ol><li><p>\u62A5\u8868\u5C06\u6839\u636E\u62A5\u8868\u7684\u5171\u4EAB\u72B6\u6001\u5728\u201C\u62A5\u8868\u4E2D\u5FC3\u201D\u4E2D\u53EF\u89C1\u3002<br> Reports will be visible in the Report Center based on the sharing status of a report.</p></li><li><p>\u7528\u6237\u5FC5\u987B\u662F\u62A5\u8868\u6240\u6709\u8005\u6216\u5177\u6709\u201C\u753B\u5E03\u8BBE\u8BA1\u8005\u7BA1\u7406\u5458\u201D\u6743\u9650\u624D\u80FD\u7F16\u8F91\u6216\u5171\u4EAB\u62A5\u8868\u3002<br> User must be the Report Owner or have the Canvas Designer Admin permission to edit or share the report.</p></li></ol></li><li><p>\u529F\u80FD\u677F\u5757\u548C\u4FE1\u606F\u9762\u677F\uFF1A<br> Tiles and Dashboards:<br> \u7528\u6237\u5FC5\u987B\u5177\u6709\u7BA1\u7406\u5458\u6743\u9650&gt;\u7BA1\u7406\u6570\u636E\u4E2D\u5FC3/ \u62A5\u8868&gt;\u5206\u6790\u529F\u80FD\u677F\u5757\u548C\u4FE1\u606F\u9762\u677F\u6743\u9650\u624D\u80FD\u7F16\u8F91\u62FC\u8D34\u548C\u4EEA\u8868\u76D8\uFF0C\u5E76\u5171\u4EAB\u4EEA\u8868\u76D8\u3002<br> User must have Administrator Permissions &gt; Manage Dashboards / Reports &gt; Analytics Tiles and Dashboards permission to edit tiles and dashboards and to share dashboards.</p></li><li><p>\u8868\u683C\u62A5\u8868\uFF08\u4EE5\u524D\u6765\u81EA\u7279\u522B\u62A5\u8868\uFF09\u3002<br> Table Reports (previously from Ad Hoc Report).<br> \u7528\u6237\u5FC5\u987B\u662F\u201C\u62A5\u8868\u6240\u6709\u8005\u201D\u6216\u5177\u6709\u201C\u62A5\u8868\u7BA1\u7406 - \u8BBF\u95EE\u6240\u6709\u62A5\u8868\u201D\u6743\u9650\u624D\u80FD\u7F16\u8F91\u6216\u5171\u4EAB\u62A5\u8868\u3002<br> The user must be the Report Owner or have the Report Admin - Access All Report permission to edit or share reports.</p></li><li><p>\u6545\u4E8B\u62A5\u8868<br> Story Reports</p><ul><li>\u7528\u6237\u5FC5\u987B\u662F\u62A5\u8868\u6240\u6709\u8005\uFF0C\u4F7F\u62A5\u8868\u4E0E\u201C\u7F16\u8F91\u201D\u8BBF\u95EE\u6743\u9650\u5171\u4EAB\uFF0C\u6216\u8005\u5177\u6709\u201C\u6545\u4E8B\u7BA1\u7406\u5458\u201D\u6743\u9650\u624D\u80FD\u7F16\u8F91\u62A5\u8868\u3002</li><li>\u7528\u6237\u5FC5\u987B\u662F\u62A5\u8868\u6240\u6709\u8005\uFF0C\u62E5\u6709\u901A\u8FC7\u201C\u5B8C\u5168\u201D\u8BBF\u95EE\u6743\u9650\u5171\u4EAB\u7684\u62A5\u8868\uFF0C\u6216\u8005\u5177\u6709\u201C\u6545\u4E8B\u7BA1\u7406\u5458\u201D\u6743\u9650\u624D\u80FD\u5171\u4EAB\u62A5\u8868\u3002</li></ul></li></ul><h3 id="\u5206\u914D\u9644\u52A0\u7528\u6237\u6743\u9650-assigning-additional-user-permissions" tabindex="-1"><a class="header-anchor" href="#\u5206\u914D\u9644\u52A0\u7528\u6237\u6743\u9650-assigning-additional-user-permissions" aria-hidden="true">#</a> \u5206\u914D\u9644\u52A0\u7528\u6237\u6743\u9650 Assigning Additional User Permissions</h3><p><img src="'+g+'" alt="Create and Run Report"></p><p>\u60A8\u53EF\u4EE5\u4E3A\u7528\u6237\u5206\u914D\u521B\u5EFA\u548C\u8FD0\u884C\u4E0D\u540CSAP SuccessFactors \u62A5\u8868\u7C7B\u578B\u7684\u529F\u80FD\u3002\u8981\u4E3A\u7528\u6237\u5206\u914D\u5728\u6240\u6709\u6216\u7279\u5B9A\u6A21\u5757\uFF08\u4E5F\u79F0\u4E3A\u62A5\u8868\u57DF\uFF09\u4E0A\u521B\u5EFA\u548C/ \u6216\u8FD0\u884C\u62A5\u8868\u7684\u529F\u80FD\uFF1A</p><ol><li><p>\u4F7F\u7528\u201C\u64CD\u4F5C\u641C\u7D22\u201D\u5BFC\u822A\u5230\u201C\u7BA1\u7406\u6743\u9650\u89D2\u8272\u201D<br> Use the Action Search to navigate to Manage Permission Roles</p></li><li><p>\u9009\u62E9\u6743\u9650\u89D2\u8272<br> Select the Permission Role</p></li><li><p>\u5355\u51FB\u6743\u9650<br> Click Permission</p></li><li><p>\u8F6C\u5230 \u201C\u7528\u6237\u6743\u9650\u201D\u2192 \u201C\u62A5\u8868\u6743\u9650\u201D<br> Go to User Permissions Reports Permission</p></li><li><p>\u9009\u62E9\u76F8\u5E94\u6743\u9650\uFF1A<br> Select the appropriate permission:</p><ol><li><p>\u8981\u5141\u8BB8\u521B\u5EFA\u65B0\u62A5\u8868\uFF0C\u8BF7\u9009\u62E9 \u201C\u521B\u5EFA\u62A5\u8868\u201D\uFF0C\u7136\u540E\u9009\u62E9\u8981\u57FA\u4E8E\u5176\u521B\u5EFA\u62A5\u8868\u7684\u6A21\u5757<br> To allow creating new reports, select Create Report and select the modules based on which you want to create reports</p></li><li><p>\u8981\u5141\u8BB8\u8FD0\u884C\u62A5\u8868\uFF0C\u8BF7\u9009\u62E9\u201C\u8FD0\u884C\u62A5\u8868\u201D\u5E76\u9009\u62E9\u8981\u8FD0\u884C\u62A5\u8868\u7684\u6A21\u5757<br> To allow running reports, select Run Report and select the modules based on which you want to run reports</p></li></ol></li><li><p>\u5355\u51FB\u5B8C\u6210<br> Click Done</p></li></ol><p>\u5982\u679C\u9009\u62E9\u7279\u5B9A\u57DF\uFF0C\u4F8B\u5982\u201C\u85AA\u916C\u8BA1\u5212\u201D\uFF0C\u5982\u679C\u5728\u201C\u4EBA\u5458\u201D\u90E8\u5206\u4E2D\u9009\u62E9\u201C\u5176\u4ED6\u8FC7\u6EE4\u5668\u201D\uFF0C\u60A8\u5C06\u770B\u5230\u57FA\u4E8E RBP\u76EE\u6807\u7FA4\u4F53\u7684\u85AA\u916C\u8BE6\u7EC6\u4FE1\u606F\u6570\u636E\u3002</p><h3 id="\u753B\u5E03\u62A5\u8868\u6743\u9650-canvas-report-permissions" tabindex="-1"><a class="header-anchor" href="#\u753B\u5E03\u62A5\u8868\u6743\u9650-canvas-report-permissions" aria-hidden="true">#</a> \u753B\u5E03\u62A5\u8868\u6743\u9650 Canvas Report Permissions</h3><p><img src="'+b+'" alt="Canvas Report Permissions"></p><p>\u8981\u4E3A\u7528\u6237\u5206\u914D\u521B\u5EFA\u548C/ \u6216\u8FD0\u884C\u753B\u5E03\u62A5\u8868\u7C7B\u578B\u7684\u529F\u80FD\uFF1A</p><ol><li><p>\u4F7F\u7528\u201C\u64CD\u4F5C\u641C\u7D22\u201D\u5BFC\u822A\u5230\u201C\u7BA1\u7406\u6743\u9650\u89D2\u8272\u201D<br> Use the Action Search to navigate to Manage Permission Roles</p></li><li><p>\u9009\u62E9\u6743\u9650\u89D2\u8272<br> Select the Permission Role</p></li><li><p>\u5355\u51FB\u6743\u9650<br> Click Permission</p></li><li><p>\u8F6C\u5230\u7528\u6237\u6743\u9650\u5206\u6790\u6743\u9650<br> Go to User Permissions Analytics Permission</p></li><li><p>\u9009\u62E9\u76F8\u5E94\u6743\u9650\uFF1A<br> Select the appropriate permission:</p><ol><li><p>\u753B\u5E03\u8BBE\u8BA1\u5668\uFF1A\u5141\u8BB8\u7528\u6237\u521B\u5EFA\u65B0\u62A5\u8868\u5E76\u8FD0\u884C\u62A5\u8868\u3002<br> Canvas Designer:allow the user to create a new report and run the report.</p></li><li><p>\u753B\u5E03\u8BBE\u8BA1\u5668\u7BA1\u7406\u5458\uFF1A\u5411\u7528\u6237\u5206\u914D\u7BA1\u7406\u5458\u6743\u9650\uFF0C\u4EE5\u4FBF\u4ED6\u4EEC\u53EF\u4EE5\u7F16\u8F91\u3001\u5220\u9664\u3001\u590D\u5236\u3001\u5171\u4EAB\u3001\u6807\u7B7E\u548C\u5BFC\u51FA\u753B\u5E03\u62A5\u8868\u3002<br> Canvas Designer Admina: ssign Admin permissions to the user so that they can edit, delete, copy, share, label, and export the Canvas Report.</p></li></ol></li><li><p>\u5355\u51FB\u5B8C\u6210<br> Click Done</p></li></ol><h3 id="\u5206\u6790\u529F\u80FD\u677F\u5757\u548C\u4FE1\u606F\u9762\u677F\u6743\u9650-analytics-tiles-and-dashboards-permission" tabindex="-1"><a class="header-anchor" href="#\u5206\u6790\u529F\u80FD\u677F\u5757\u548C\u4FE1\u606F\u9762\u677F\u6743\u9650-analytics-tiles-and-dashboards-permission" aria-hidden="true">#</a> \u5206\u6790\u529F\u80FD\u677F\u5757\u548C\u4FE1\u606F\u9762\u677F\u6743\u9650 Analytics Tiles and Dashboards Permission</h3><p><img src="'+R+'" alt="Analytics Tiles and Dashboards Permission"></p><p>\u8981\u4E3A\u7528\u6237\u5206\u914D\u8FD0\u884C\u529F\u80FD\u529F\u80FD\u548C\u201C\u6807\u51C6\u4FE1\u606F\u9762\u677F\u201D\u62A5\u8868\u7C7B\u578B\uFF1A</p><ol><li><p>\u4F7F\u7528\u201C\u64CD\u4F5C\u641C\u7D22\u201D\u5BFC\u822A\u5230\u201C\u7BA1\u7406\u6743\u9650\u89D2\u8272\u201D<br> Use the Action Search to navigate to Manage Permission Roles</p></li><li><p>\u9009\u62E9\u6743\u9650\u89D2\u8272<br> Select the Permission Role</p></li><li><p>\u5355\u51FB\u6743\u9650<br> Click Permission</p></li><li><p>\u8F6C\u5230\u7528\u6237\u6743\u9650\u5206\u6790\u6743\u9650<br> Go to User Permissions Analytics Permission</p></li><li><p>\u542F\u7528\u5206\u6790\u529F\u80FD\u677F\u5757\u548C\u4FE1\u606F\u9762\u677F- \u5141\u8BB8\u7528\u6237\u8FD0\u884C\u6240\u6709\u6216\u7279\u5B9A\u529F\u80FD\u677F\u5757\u548C\u6807\u51C6\u4EEA\u8868\u76D8<br> Enable Analytics Tiles and Dashboards - to allow a user to run all or certain tiles and standard dashboards</p></li><li><p>\u5355\u51FB\u5B8C\u6210<br> Click Done</p></li></ol><div class="custom-container warning"><p class="custom-container-title">Note</p><p>\u67E5\u770BHR882 \u2013 SAP SuccessFactors People Analytics\uFF1A\u62A5\u8868\u548C\u7BA1\u7406\uFF0C\u83B7\u53D6\u8BE6\u7EC6\u6743\u9650\u77E9\u9635\uFF0C\u4EE5\u5728\u201C\u62A5\u8868\u4E2D\u5FC3\u201D\u4E2D\u521B\u5EFA\u548C\u7BA1\u7406\u4E0D\u540C\u7684\u62A5\u8868\u3002</p></div><h3 id="\u7EC3\u4E60\u4E00-\u62A5\u8868\u4E2D\u5FC3\u6743\u9650-report-center-permission" tabindex="-1"><a class="header-anchor" href="#\u7EC3\u4E60\u4E00-\u62A5\u8868\u4E2D\u5FC3\u6743\u9650-report-center-permission" aria-hidden="true">#</a> \u{1F389}\u7EC3\u4E60\u4E00 \u62A5\u8868\u4E2D\u5FC3\u6743\u9650 Report Center Permission</h3><p>Give the necessary permissions to use the Report Center.</p><ol><li><p>Give your administrator role the permission to use reporting.</p><ol><li>From the Action Search go to Manage Permission Roles</li><li>Open your administrator role and click on Permission...</li><li>Go to the category Reports Permissions and click on Select All</li><li>Verify that in the list you have Report Center and Schedule Reports</li><li>Go to the category Manage Dashboard /Reports and click on Select All</li><li>Click Done and Save Changes</li></ol></li><li><p>Create a manager role and give them the ability to run reports and to access the Report Center</p><ol><li>In Manage Permission Roles, click on Create New.</li><li>The role name is All Managers</li><li>Click on Permissions\u2026 and click on Report Permissions</li><li>Select the following options: <ul><li>Report Center</li><li>Schedule Reports</li><li>Run Report Make sure that the radio button is selected for <em>all</em> domains</li></ul></li><li>Click on General User Permission and select User Login. Note that this permission is generally given to the all employee role and it is needed to run reports.</li><li>Click Done.</li><li>Add the granting by clicking on the button Add\u2026</li><li>Grant this role to Managers.</li><li>To specify the target population, make sure that you have the radio button for Granted User\u2019s Direct Reports.</li><li>Select the check box to Exclude Granted User from having the permission access to him/herself.</li><li>Click Done and Save Changes</li><li>Log out and go back to your instance.</li></ol></li></ol>',44);function G(F,L){const i=n("RouterLink");return a(),l(p,null,[_,e("nav",P,[e("ul",null,[e("li",null,[t(i,{to:"#\u62A5\u8868\u4E2D\u5FC3-report-center"},{default:s(()=>[v]),_:1}),e("ul",null,[e("li",null,[t(i,{to:"#\u62A5\u8868\u4E2D\u5FC3-\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-report-center-configuration-check-tool"},{default:s(()=>[f]),_:1})])])]),e("li",null,[t(i,{to:"#\u4E3A\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3\u5206\u914D\u7BA1\u7406\u6743\u9650-assigning-administration-permissions-to-manage-the-report-center"},{default:s(()=>[S]),_:1}),e("ul",null,[e("li",null,[t(i,{to:"#\u7BA1\u7406\u62A5\u8868\u4E2D\u5FC3-manage-report-center"},{default:s(()=>[y]),_:1})]),e("li",null,[t(i,{to:"#\u62A5\u8868\u4E2D\u5FC3\u641C\u7D22\u672A\u8FD4\u56DE\u6240\u6709\u8D39\u7528\u62A5\u8868-report-center-search-not-returning-all-reports"},{default:s(()=>[A]),_:1})]),e("li",null,[t(i,{to:"#\u5C06\u7528\u6237\u8BBF\u95EE\u6743\u9650\u5206\u914D\u5230\u62A5\u8868\u4E2D\u5FC3-assigning-user-access-to-the-report-center"},{default:s(()=>[w]),_:1})]),e("li",null,[t(i,{to:"#\u4E86\u89E3\u62A5\u8868\u4E2D\u5FC3\u6743\u9650-understanding-the-report-center-permission"},{default:s(()=>[k]),_:1})]),e("li",null,[t(i,{to:"#\u5206\u914D\u9644\u52A0\u7528\u6237\u6743\u9650-assigning-additional-user-permissions"},{default:s(()=>[T]),_:1})]),e("li",null,[t(i,{to:"#\u753B\u5E03\u62A5\u8868\u6743\u9650-canvas-report-permissions"},{default:s(()=>[x]),_:1})]),e("li",null,[t(i,{to:"#\u5206\u6790\u529F\u80FD\u677F\u5757\u548C\u4FE1\u606F\u9762\u677F\u6743\u9650-analytics-tiles-and-dashboards-permission"},{default:s(()=>[U]),_:1})]),e("li",null,[t(i,{to:"#\u7EC3\u4E60\u4E00-\u62A5\u8868\u4E2D\u5FC3\u6743\u9650-report-center-permission"},{default:s(()=>[D]),_:1})])])])])]),M],64)}var N=o(C,[["render",G],["__file","Lesson2.html.vue"]]);export{N as default};
