import{_ as r,r as n,o as c,c as h,a as e,b as t,w as s,F as u,d as o,e as a}from"./app.be1e3df6.js";var d="/THR81/assets/20220509175113.3dc7eaa3.png";const p={},m=e("h1",{id:"\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177","aria-hidden":"true"},"#"),o(" \u914D\u7F6E\u68C0\u67E5\u5DE5\u5177")],-1),f={class:"table-of-contents"},g=o("\u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177 Using Configuration Check Tool"),k=o("\u4E3A\u4EC0\u4E48\u5982\u6B64\u91CD\u8981\uFF1F"),_=o("\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177\u7684\u6743\u9650"),y=o("\u{1F389}\u7EC3\u4E60\u4E00 \u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177 Use the Configuration Check Tool"),b=a('<div class="custom-container tip"><p class="custom-container-title">\u672C\u8282\u76EE\u6807</p><ul><li>\u4E86\u89E3\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177</li></ul></div><h2 id="\u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-using-configuration-check-tool" tabindex="-1"><a class="header-anchor" href="#\u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-using-configuration-check-tool" aria-hidden="true">#</a> \u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177 Using Configuration Check Tool</h2><p>\u5982\u679C\u60A8\u7684SAP SuccessFactors \u5E94\u7528\u7A0B\u5E8F\u4EE5\u610F\u5916\u65B9\u5F0F\u8FD0\u884C\uFF0C\u5219\u53EF\u80FD\u5B58\u5728\u914D\u7F6E\u6216\u6570\u636E\u51B2\u7A81\uFF1A\u60A8\u6709\u4E00\u4E9B\u6570\u636E\u4E0D\u4E00\u81F4\u6216\u914D\u7F6E\u9519\u8BEF\u3002\u68C0\u67E5\u5DE5\u5177\u53EF\u5FEB\u901F\u8BC6\u522B\u8FD9\u4E9B\u7C7B\u578B\u7684\u95EE\u9898\uFF0C\u4ECE\u800C\u53EF\u4EE5\u907F\u514D\u652F\u6301\u5DE5\u5355(\u63D0\u7968)\u3002\u5982\u679C\u95EE\u9898\u5F88\u4E25\u91CD\uFF0C\u60A8\u53EF\u80FD\u4ECD\u9700\u8981\u521B\u5EFA\u652F\u6301\u5DE5\u5355\uFF0C\u4F46\u5728\u4E25\u91CD\u60C5\u51B5\u4E0B\uFF0C\u68C0\u67E5\u5DE5\u5177\u4E5F\u4F1A\u4E3A\u60A8\u8282\u7701\u65F6\u95F4\uFF0C\u56E0\u4E3A\u68C0\u67E5\u5DE5\u5177\u53EF\u4EE5\u5BFC\u51FA\u68C0\u67E5\u7684\u7ED3\u679C\u548C\u60A8\u7684\u914D\u7F6E\u3002\u56E0\u6B64\uFF0C\u652F\u6301\u5DE5\u7A0B\u5E08\u53EF\u4EE5\u66F4\u5FEB\u5730\u8BC6\u522B\u95EE\u9898\u3002</p><p>\u51FA\u73B0\u6B64\u7C7B\u95EE\u9898\u7684\u539F\u56E0\u53EF\u80FD\u6709\u591A\u79CD\uFF1A</p><ul><li><p>\u67D0\u4E9B\u6570\u636E\u4E0D\u4E00\u81F4<br> Some data is inconsistent</p></li><li><p>\u7CFB\u7EDF\u672A\u6B63\u786E\u914D\u7F6E<br> The system is not configured correctly</p></li></ul><p>\u4F7F\u7528\u68C0\u67E5\u5DE5\u5177\uFF0C\u60A8\u53EF\u4EE5\u8BC6\u522B\u9519\u8BEF\u5185\u5BB9\uFF0C\u800C\u4E0D\u662F\u7B80\u5355\u5730\u521B\u5EFA\u652F\u6301\u5DE5\u5355\u3002\u68C0\u67E5\u5DE5\u5177\uFF1A</p><ul><li><p>\u627E\u51FA\u95EE\u9898\u6240\u5728\uFF0C\u95EE\u9898\u6709\u591A\u7CDF\u7CD5<br> Finds out what is wrong and how bad the problem is</p></li><li><p>\u5EFA\u8BAE\u89E3\u51B3\u65B9\u6848<br> Suggests a solution</p></li></ul><p>\u4FDD\u7559\u68C0\u67E5\u7ED3\u679C\u7684\u5386\u53F2\u8BB0\u5F55\uFF0C\u7528\u6237\u53EF\u4EE5\u5728\u68C0\u67E5\u5DE5\u5177\u7528\u6237\u754C\u9762\u4E2D\u67E5\u770B\u68C0\u67E5\u8FD0\u884C\u7684\u5386\u53F2\u8BB0\u5F55\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u68C0\u67E5\u5DE5\u5177\u8FD8\u53EF\u4EE5\u751F\u6210\u6240\u6709\u53EF\u7528\u68C0\u67E5\u7684\u5217\u8868\u3002\u8FD9\u901A\u8FC7\u68C0\u67E5\u5DE5\u5177\u4E2D\u7684\u5E2E\u52A9\u529F\u80FD\u63D0\u4F9B\uFF0C\u5E76\u63D0\u4F9B\u6240\u6709\u68C0\u67E5\u7684\u9AD8\u7EA7\u6C47\u603B\u3002\u4E0D\u4EC5\u9650\u4E8E\u7528\u6237\u6709\u6743\u8BBF\u95EE\u7684\u68C0\u67E5\u3002</p><h3 id="\u4E3A\u4EC0\u4E48\u5982\u6B64\u91CD\u8981" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u5982\u6B64\u91CD\u8981" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u5982\u6B64\u91CD\u8981\uFF1F</h3><p>\u6B64\u5217\u8868\u4E3A\u60F3\u8981\u4E86\u89E3\u68C0\u67E5\u5DE5\u5177\u5B8C\u6574\u529F\u80FD\u7684\u4EFB\u4F55\u7528\u6237\u63D0\u4F9B\u6709\u7528\u7684\u4EA7\u54C1\u5185\u652F\u6301\u6587\u6863\u6458\u8981\u3002\u4E0D\u4F1A\u5411\u7528\u6237\u63D0\u4F9B\u901A\u5E38\u4E0D\u4F1A\u901A\u8FC7\u6807\u51C6SAP \u5E2E\u52A9\u6587\u6863\u6E20\u9053\u5171\u4EAB\u7684\u4FE1\u606F\u3002</p><h3 id="\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177\u7684\u6743\u9650" tabindex="-1"><a class="header-anchor" href="#\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177\u7684\u6743\u9650" aria-hidden="true">#</a> \u914D\u7F6E\u68C0\u67E5\u5DE5\u5177\u7684\u6743\u9650</h3><p>\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177\u5177\u6709\u81EA\u5DF1\u7684\u6743\u9650\u3002\u7279\u5B9A\u6743\u9650\u5305\u62EC\uFF1A</p><ul><li><p>\u8BBF\u95EE\u68C0\u67E5\u5DE5\u5177\u6743\u9650\u6388\u6743\u7528\u6237\u8BBF\u95EE\u5DE5\u5177\u3002<br> The Access Check Tool permission authorizes users to access the tool.</p></li><li><p>\u201C\u5141\u8BB8\u914D\u7F6E\u5BFC\u51FA\u201D\u6743\u9650\u6388\u6743\u7528\u6237\u5728\u9700\u8981\u521B\u5EFA\u652F\u6301\u6D88\u606F\u7684\u60C5\u51B5\u4E0B\u5C06\u914D\u7F6E\u4FE1\u606F\u9644\u52A0\u5230\u652F\u6301\u6D88\u606F\u3002<br> The Allow Configuration Export permission authorizes users to attach configuration information to a ticket in cases where they need to create one.</p></li><li><p>\u201C\u5141\u8BB8\u68C0\u67E5\u5DE5\u5177\u5FEB\u901F\u4FEE\u590D\u201D\u6743\u9650\u6388\u4E88\u7528\u6237\u4FEE\u590D\u914D\u7F6E\u548C\u6570\u636E\u95EE\u9898\u7684\u6743\u9650\u3002<br> Allow Check Tool Quick Fix permission authorizes users to fix configuration and data issues.</p></li></ul><p>\u8981\u8BBE\u7F6E\u6240\u9700\u7684\u6743\u9650\uFF0C\u8BF7\u8F6C\u5230\u7BA1\u7406\u5458\u4E2D\u5FC3\u5E76\u9009\u62E9Set User Permissions &gt; Manage Permission Roles\u3002\u9009\u62E9\u8981\u7EF4\u62A4\u6743\u9650\u7684\u89D2\u8272\uFF0C\u9009\u62E9\u6743\u9650\uFF0C\u7136\u540E\u5411\u4E0B\u6EDA\u52A8\u76F4\u5230\u770B\u5230\u68C0\u67E5\u5DE5\u5177\u89D2\u8272\u3002\u8FDB\u884C\u9002\u5F53\u7684\u8BBE\u7F6E\u3002</p>',15),w=o("\u201C\u5728\u68C0\u67E5\u5DE5\u5177\u4E2D\u8FD0\u884C\u68C0\u67E5(Run a check in the check tool)\u201D\u89C6\u9891\u94FE\u63A5\uFF1A"),v={href:"https://sapvideoa35699dc5.hana.ondemand.com/?entry_id=1_qvfxamwa",target:"_blank",rel:"noopener noreferrer"},C=o("https://sapvideoa35699dc5.hana.ondemand.com/?entry_id=1_qvfxamwa"),T=a('<h3 id="\u7EC3\u4E60\u4E00-\u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-use-the-configuration-check-tool" tabindex="-1"><a class="header-anchor" href="#\u7EC3\u4E60\u4E00-\u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-use-the-configuration-check-tool" aria-hidden="true">#</a> \u{1F389}\u7EC3\u4E60\u4E00 \u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177 Use the Configuration Check Tool</h3><p>Some of your colleagues are reporting errors when running ad-hoc reports. Your task is to troubleshoot and resolve the issue or raise a ticket.</p><p>Before you start, make sure you have the required permissions to access the Configuration Check tool as described in previous lesson. If needed, add permissions to Administrators group. Remember: Once permissions are added, you will need to log out and log back into the system for changes to take effect.</p><ol><li><p>Give your administrator role all the necessary permissions to fully use the Check Tool.</p><ol><li>From the Action Search, go to Manage Permission Roles.</li><li>Open your administrators role and click on Permissions.</li><li>Find the category Check Tool and select all the available permissions.</li><li>Click Done, Save Changes, log out and come back to your instance.</li></ol></li><li><p>Access the Check tool.<br> Use the Action Search to navigate to Check tool.</p></li><li><p>Run the check of your Role-Based Permissions.</p><ol><li>From the Application dropdown, select the Role-Based Permissions to run a check for.</li><li>The list of checks is automatically updated depending on the application selected.</li><li>Select all of the rows.</li><li>Click \u201Crun X checks\u201D on the bottom right.</li></ol><div class="custom-container warning"><p class="custom-container-title">Note</p><p>you can run as many checks as you want. The Run Checks button at bottom right gets updated to tell you how many you have selected.</p></div></li><li><p>Look at the Detailed results and decide what next steps to take.<br> On completion of the check, the Configuration Check tool displays results. The results can be any of these:</p><ul><li>No inconsistencies found. All checks have successfully passed.</li><li>X inconsistences found, where &quot;X&quot; is the number of inconsistencies. In cases where inconsistences are found, you see either a yellow warning icon or a red alarm icon.</li><li>The yellow icon is a warning that something not too serious is wrong. The system proposes a solution.</li><li>The red icon indicates that the problem is serious. You must take action, which might include creating a Support ticket.</li><li>If creating a customer incident make sure it is under the relevant category, and do provide the component value you copied from the Need Assistance? Section as shown below: <img src="'+d+'" alt="Detailed Results"></li></ul></li></ol>',4);function x(S,A){const i=n("RouterLink"),l=n("ExternalLinkIcon");return c(),h(u,null,[m,e("nav",f,[e("ul",null,[e("li",null,[t(i,{to:"#\u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-using-configuration-check-tool"},{default:s(()=>[g]),_:1}),e("ul",null,[e("li",null,[t(i,{to:"#\u4E3A\u4EC0\u4E48\u5982\u6B64\u91CD\u8981"},{default:s(()=>[k]),_:1})]),e("li",null,[t(i,{to:"#\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177\u7684\u6743\u9650"},{default:s(()=>[_]),_:1})]),e("li",null,[t(i,{to:"#\u7EC3\u4E60\u4E00-\u4F7F\u7528\u914D\u7F6E\u68C0\u67E5\u5DE5\u5177-use-the-configuration-check-tool"},{default:s(()=>[y]),_:1})])])])])]),b,e("p",null,[w,e("a",v,[C,t(l)])]),T],64)}var F=r(p,[["render",x],["__file","Lesson2.html.vue"]]);export{F as default};
