import{_ as s,r as n,o as r,c as l,a as e,b as a,w as o,F as c,d as t,e as d}from"./app.914ea516.js";var b="/THR81/assets/20220508153123.5f4d18f0.png",m="/THR81/assets/20220508153236.20c07804.png",h="/THR81/assets/20220508154009.3b8360b8.png",p="/THR81/assets/20220508154046.c3b6a451.png",u="/THR81/assets/20220508154153.c38b2fea.png";const f={},g=e("h1",{id:"\u7BA1\u7406\u79FB\u52A8\u8BBE\u7F6E",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u7BA1\u7406\u79FB\u52A8\u8BBE\u7F6E","aria-hidden":"true"},"#"),t(" \u7BA1\u7406\u79FB\u52A8\u8BBE\u7F6E")],-1),v={class:"table-of-contents"},M=t("\u79FB\u52A8\u8BBE\u7F6E\u7BA1\u7406 Management of Mobile Settings"),S=t("\u79FB\u52A8\u529F\u80FD\u542F\u7528 Mobile Feature Enablement"),_=t("\u79FB\u52A8\u8BBF\u95EE\u6743\u9650\u7684\u9009\u62E9 Selection of Mobile Access Permissions"),P=t("\u79FB\u52A8\u7535\u5B50\u90AE\u4EF6\u901A\u77E5 Mobile Email Notification"),A=t("\u79FB\u52A8\u8BBE\u5907\u6FC0\u6D3B Mobile Device Activation"),y=t("\u79FB\u52A8\u7528\u6237\u7BA1\u7406 Mobile User Management"),w=t("\u{1F389}\u7EC3\u4E60\u4E5D\u5982\u4F55\u542F\u7528\u79FB\u52A8\u8BBE\u5907\u7528\u6237\u7684\u8BBF\u95EE How to Enable Access for a Mobile Device User"),E=d('<div class="custom-container tip"><p class="custom-container-title">\u672C\u8282\u76EE\u6807</p><ul><li>\u7BA1\u7406\u79FB\u52A8\u8BBE\u7F6E</li></ul></div><h2 id="\u79FB\u52A8\u8BBE\u7F6E\u7BA1\u7406-management-of-mobile-settings" tabindex="-1"><a class="header-anchor" href="#\u79FB\u52A8\u8BBE\u7F6E\u7BA1\u7406-management-of-mobile-settings" aria-hidden="true">#</a> \u79FB\u52A8\u8BBE\u7F6E\u7BA1\u7406 Management of Mobile Settings</h2><p><img src="'+b+'" alt="Admin Center: Mobile"></p><p>SAP SuccessFactors \u76EE\u524D\u652F\u6301\u5E02\u573A\u4E0A\u6700\u53D7\u6B22\u8FCE\u7684\u79FB\u52A8\u5E73\u53F0\u3002\u4F5C\u4E3A\u7BA1\u7406\u5458\uFF0C\u60A8\u53EF\u4EE5\u542F\u7528\u6216\u7981\u7528\u516C\u53F8\u5458\u5DE5\u7684\u79FB\u52A8\u8BBE\u5907\u8BBE\u7F6E\u3002\u79FB\u52A8\u529F\u80FD\u53EF\u901A\u8FC7 SAP SuccessFactors Human eXperience Management (HXM) \u5E94\u7528\u8BBF\u95EE\u3002</p><p>\u79FB\u52A8\u7BA1\u7406\u5728\u79FB\u52A8\u7BA1\u7406\u9762\u677F(Mobile Administration Pane)\u4E2D\u7B80\u5355\u5316\u548C\u6C47\u603B\u5316\u3002\u5728\u6B64\u9762\u677F\u4E2D\uFF0C\u60A8\u5C06\u770B\u5230\u56DB\u4E2A\u4E3B\u8981\u529F\u80FD\uFF0C\u53EF\u5E2E\u52A9\u60A8\u90E8\u7F72\u548C\u7BA1\u7406\u516C\u53F8\u7684 Mobile\uFF1A</p><ul><li><p>\u542F\u7528\u79FB\u52A8\u529F\u80FD<br> Enable Mobile Features</p></li><li><p>\u7BA1\u7406\u6743\u9650\u89D2\u8272<br> Manage Permission Roles</p></li><li><p>\u7535\u5B50\u90AE\u4EF6\u6A21\u677F\u901A\u77E5<br> Email Template Notifications</p></li><li><p>\u7BA1\u7406\u79FB\u52A8\u7528\u6237<br> Manage Mobile Users</p></li></ul><p>\u5728\u672C\u8282\u4E2D\uFF0C\u60A8\u5C06\u4E86\u89E3\u5982\u4F55\u542F\u7528\u79FB\u52A8\u529F\u80FD\u3001\u9009\u62E9\u79FB\u52A8\u8BBF\u95EE\u6743\u9650\u4EE5\u53CA\u5982\u4F55\u505C\u7528\u79FB\u52A8\u8BBE\u5907\u3002</p><h3 id="\u79FB\u52A8\u529F\u80FD\u542F\u7528-mobile-feature-enablement" tabindex="-1"><a class="header-anchor" href="#\u79FB\u52A8\u529F\u80FD\u542F\u7528-mobile-feature-enablement" aria-hidden="true">#</a> \u79FB\u52A8\u529F\u80FD\u542F\u7528 Mobile Feature Enablement</h3><p><img src="'+m+'" alt="Mobile Feature Enablement"></p><p>\u901A\u8FC7\u8F6C\u5230Action Search &gt; Enable Mobile Features \uFF0C\u5E76\u9009\u62E9\u8981\u542F\u7528\u7684\u79FB\u52A8\u529F\u80FD\u6765\u542F\u7528\u79FB\u52A8\u8BBE\u7F6E\u3002\u6B64\u529F\u80FD\u53EF\u5E2E\u52A9\u60A8\u9009\u62E9\u53EF\u7528\u4E8E\u79FB\u52A8\u7528\u6237\u7684\u6A21\u5757\u3002\u529F\u80FD\u5305\u62EC\uFF1A\u4F7F\u7528\u79FB\u52A8\u8BBF\u95EE\u4EE3\u7801\u8BBF\u95EE\u79FB\u52A8\u8BBE\u5907\u548C\u542F\u7528\u79FB\u52A8\u5E7F\u544A\u3002</p><p>\u542F\u7528\u79FB\u52A8\u529F\u80FD(Enable Mobile Features) \u5177\u6709\u4EE5\u4E0B\u4E0D\u540C\u90E8\u5206\uFF1A</p><ul><li><p>\u79FB\u52A8\u7279\u5B9A\uFF1A \u672C\u90E8\u5206\u4E2D\u7684\u529F\u80FD\u5141\u8BB8\u60A8\u67E5\u770B\u548C\u63A7\u5236\u57FA\u672C\u79FB\u52A8\u529F\u80FD\u3002\uFF08\u4F8B\u5982\uFF0C\u4E3B\u9898\u5316\u3001\u8BBE\u5907\u652F\u6301\u3001 PIN \u7B49\uFF09<br> Mobile Specific: The features in this section allow you to view and control basic mobile functionality. (For example, Theming, on device support, PIN, and so on)</p></li><li><p>\u6A21\u5757\uFF1A \u5728\u6B64\u90E8\u5206\u4E2D\uFF0C\u60A8\u53EF\u4EE5\u67E5\u770B\u5F53\u524D\u5728\u5B9E\u4F8B\u4E2D\u90E8\u7F72\u4E14\u79FB\u52A8\u529F\u80FD\u53EF\u7528\u7684\u6240\u6709 SAP SuccessFactors \u6A21\u5757\u3002<br> Modules: In this section, you can view all the SAP SuccessFactors modules that are currently deployed in your instance and have mobile capabilities available.</p></li></ul><p>\u9009\u4E2D\u8BE5\u590D\u9009\u6846\u5C06\u4E3A\u6240\u6709\u6388\u6743\u79FB\u52A8\u7528\u6237\u5F00\u542F\u8BE5\u529F\u80FD\u3002\u53D6\u6D88\u9009\u4E2D\u8BE5\u590D\u9009\u6846\u5C06\u5173\u95ED\u6240\u6709\u6388\u6743\u79FB\u52A8\u7528\u6237\u7684\u529F\u80FD\u3002</p><p>\u79FB\u52A8\u529F\u80FD\u8868</p><table><thead><tr><th>\u529F\u80FD Feature</th><th>\u63CF\u8FF0 Description</th></tr></thead><tbody><tr><td>\u4E3B\u9898\u8BBE\u7F6E<br>Theming</td><td>\u4F5C\u4E3A\u7BA1\u7406\u5458\uFF0C\u53EF\u4EE5\u5B9A\u5236 iPad BizX \u79FB\u52A8\u5E94\u7528\u7A0B\u5E8F\u7684\u6807\u9898\u989C\u8272\u548C\u6807\u9898\u5FBD\u6807\u3002\u5C06\u663E\u793A\u6240\u6709\u6700\u7EC8\u7528\u6237\u7684\u81EA\u5B9A\u4E49\u6807\u9898<br>As the admin, the header color and header logo of the iPad BizX Mobile app can be cus-tomized. The customized header will display for all End Users</td></tr><tr><td>SF\u901A\u77E5<br>SF Notification</td><td>\u542F\u7528\u4EE5\u5C06\u901A\u77E5\u53D1\u9001\u5230\u79FB\u52A8\u8BBE\u5907<br>Enable to send notifications to mobile devises</td></tr><tr><td>\u8BBE\u5907\u4E0A\u652F\u6301<br>On Device Support</td><td>\u5728 BizX \u79FB\u52A8\u5E94\u7528\u7684\u201C\u5173\u4E8E\u201D\u90E8\u5206\uFF0C\u6700\u7EC8\u7528\u6237\u53EF\u4EE5\u8054\u7CFB\u652F\u6301\u90E8\u95E8\u3002\u4F5C\u4E3A\u7BA1\u7406\u5458\uFF0C\u53EF\u4EE5\u914D\u7F6E\u8054\u7CFB\u4EBA\u9009\u9879<br>In the &quot;About&quot; section of the BizX Mobile app, End Users can contact support. As the ad-min, the contact options can be configured</td></tr><tr><td>\u79FB\u52A8\u5BC6\u7801<br>Mobile Password</td><td>\u6700\u7EC8\u7528\u6237\u5C06\u9700\u8981\u4E3A\u6BCF\u4E2A\u79FB\u52A8\u8BBE\u5907\u521B\u5EFA\u5BC6\u7801\u3002<br>End Users will be required to create a Pass-word for each mobile device</td></tr><tr><td>\u8BBE\u5907\u5B89\u5168\u5B58\u50A8<br>On-device Secure Storage</td><td>\u4E3A\u4E86\u63D0\u9AD8\u53EF\u7528\u6027\u548C\u79BB\u7EBF\u6027\uFF0C\u6211\u4EEC\u5F3A\u70C8\u9F13\u52B1\u5C06\u8BBE\u5907\u5B58\u50A8\u4FDD\u7559\u5728 \u4E0A\uFF0C\u4EE5\u4FBF\u4E22\u5931\u8FDE\u63A5\u529F\u80FD\u3002\u5173\u95ED\u8BBE\u5907\u5B58\u50A8\u65F6\uFF0C\u4E00\u4E9B\u9879\u76EE\u4ECD\u5C06\u5728\u8BBE\u5907\u4E0A\u7F13\u5B58\uFF1A\u5B66\u4E60\u79BB\u7EBF\u6587\u6863\u548C SAP Jam \u6587\u6863<br>We strongly encourage leaving on device storage ON, in order to increase usability and offline I loss of connectivity capabilities. When turning OFF on device storage, a few items will still be cached on device: Learning off-line documents as well as SAP Jam docu-ments</td></tr><tr><td>\u7EC4\u7EC7\u7ED3\u6784\u56FE<br>Org Chart</td><td>\u6700\u7EC8\u7528\u6237\u5728 Org Chart \u4E2D\u6D4F\u89C8\uFF0C\u4E0E Web \u5BF9\u5E94\u7528\u6237\u7C7B\u4F3C<br>End Users browse through the Org Chart, similar to the web counterpart</td></tr></tbody></table><p>\u79FB\u52A8\u529F\u80FD\u7684\u6A21\u5757\u8868</p><table><thead><tr><th>\u6A21\u5757 Module</th><th>\u63CF\u8FF0 Description</th></tr></thead><tbody><tr><td>SAP Jam</td><td>\u652F\u6301\u968F\u65F6\u968F\u5730\u5F00\u5C55\u793E\u4EA4\u534F\u4F5C<br>Enable social collaboration on the go</td></tr><tr><td>\u5165\u804C<br>Onboarding</td><td>\u901A\u8FC7\u79FB\u52A8\u9996\u65E5\u4F53\u9A8C\uFF0C\u4E3A\u65B0\u5458\u5DE5\u63D0\u4F9B\u4ED6\u4EEC\u6240\u9700\u7684\u6240\u6709\u4FE1\u606F\uFF0C\u51CF\u5C11\u5BF9\u65B0\u5458\u5DE5\u7684\u7126\u8651<br>Reduce anxiety of your new hire by providing them with all the information they need through the mobile first day experience</td></tr><tr><td>\u4F11\u5047<br>Time Off</td><td>\u5458\u5DE5\u548C\u7ECF\u7406\u53EF\u4EE5\u8BB0\u5F55\u548C\u67E5\u770B\u6709\u5173\u5047\u671F\u3001\u75C5\u5047\u548C\u5176\u4ED6\u7F3A\u52E4\u7C7B\u578B\u7684\u4FE1\u606F<br>Employees and managers can record and view information on vacation, sick leaves and other absences types</td></tr><tr><td>\u798F\u5229(Beta)<br>Benefits (Beta)</td><td>\u5458\u5DE5\u53EF\u8FDB\u884C\u798F\u5229\u7533\u8BF7\u5E76\u67E5\u770B\u798F\u5229\u8054\u7CFB\u4EBA\u4FE1\u606F<br>Employees can make benefits claims and view benefits contact information</td></tr><tr><td>\u5DE5\u8D44\u62A5\u8868<br>Pay Statement</td><td>\u5458\u5DE5\u53EF\u901A\u8FC7\u79FB\u52A8\u8BBE\u5907\u8BBF\u95EE\u5F53\u524D\u548C\u4E4B\u524D\u7684\u5DE5\u8D44\u62A5\u8868\u4FE1\u606F\uFF08\u6CE8\u610F\uFF1A\u5728\u751F\u4EA7\u73AF\u5883\u4E2D\u542F\u7528\u6B64\u201C\u5DE5\u8D44\u62A5\u8868\u201D\u529F\u80FD\u4E4B\u524D\uFF0C\u8BF7\u786E\u4FDD\u5DF2\u5BF9\u5DE5\u8D44\u6838\u7B97\u7ED3\u679C\u7AEF\u5230\u7AEF\u6D41\u7A0B\u8FDB\u884C\u5185\u90E8\u6D4B\u8BD5\u548C\u9A8C\u8BC1\uFF09<br>Employees can access their current and pre-vious pay statements information from their mobile devices (Note: Please ensure you have internally tested and validated the pay-roll results end-to-end process before enable this Pay Statement feature within your pro-duction environment)</td></tr><tr><td>\u62DB\u8058<br>Recruiting</td><td>\u65E0\u9700\u56E0\u6D41\u7A0B\u7F13\u6162\u800C\u9519\u5931\u4F18\u79C0\u7684\u5019\u9009\u4EBA\uFF1A\u901A\u8FC7\u79FB\u52A8\u9762\u8BD5\u53CD\u9988\u3001\u79FB\u52A8\u804C\u4F4D\u7533\u8BF7\u548C\u79FB\u52A8\u5F55\u7528\u901A\u77E5\u5BA1\u6279\uFF0C\u652F\u6301\u5458\u5DE5\u5C3D\u5FEB\u5B8C\u6210\u62DB\u8058\u6D41\u7A0B<br>Never miss a good candidate due to a slow process: Enable your employees to keep the recruiting process moving as fast as possible with mobile interview feedback, mobile job requisitions and mobile offer approvals</td></tr><tr><td>\u76EE\u6807<br>Goals</td><td>\u4F7F\u7528\u6237\u80FD\u591F\u67E5\u770B\u548C\u7F16\u8F91\u5176\u76EE\u6807\uFF0C\u4F7F\u7ECF\u7406\u80FD\u591F\u8BBF\u95EE\u5176\u56E2\u961F\u76EE\u6807<br>Enable users to view and edit their goals and managers to access their team goals</td></tr><tr><td>\u6F14\u793A<br>Presentations</td><td>\u6700\u7EC8\u7528\u6237\u53EF\u4EE5\u521B\u5EFA\u548C\u67E5\u770B\u6709\u5173\u7279\u5B9A\u5458\u5DE5\u7684\u5B9E\u65F6\u6F14\u793A\u3002<br>End Users can create and view a real-time presentation on specific employees.</td></tr></tbody></table><h3 id="\u79FB\u52A8\u8BBF\u95EE\u6743\u9650\u7684\u9009\u62E9-selection-of-mobile-access-permissions" tabindex="-1"><a class="header-anchor" href="#\u79FB\u52A8\u8BBF\u95EE\u6743\u9650\u7684\u9009\u62E9-selection-of-mobile-access-permissions" aria-hidden="true">#</a> \u79FB\u52A8\u8BBF\u95EE\u6743\u9650\u7684\u9009\u62E9 Selection of Mobile Access Permissions</h3><p><img src="'+h+'" alt="Mobile Access Permission Settings"></p><p>\u8981\u6307\u5B9A\u54EA\u4E9B\u7528\u6237\u53EF\u4EE5\u8BBF\u95EE\u5176 SAP SuccessFactors \u8D26\u6237\uFF0C\u8BF7\u5BFC\u822A\u5230 Action Search &gt; Manage Permission Roles\uFF0C\u9009\u62E9\u8981\u6388\u4E88\u79FB\u52A8\u6743\u9650\u8BBF\u95EE\u6743\u9650\u7684\u89D2\u8272 General User Permission &gt; Mobile Access\uFF0C\u7136\u540E\u9009\u62E9\u8981\u5141\u8BB8\u7528\u6237\u8BBF\u95EE\u7684\u6743\u9650\u89D2\u8272\u3002</p><p>\u4E3A\u4E86\u5236\u5B9A\u53EF\u63A7\u5236\u7684\u63A8\u5E7F\u7B56\u7565\uFF0C\u8BF7\u9075\u5FAA\u4E0A\u8FF0\u6B65\u9AA4\uFF0C\u4E0D\u8981\u9009\u62E9 \u6240\u6709\u5458\u5DE5(All Employees) \uFF0C\u800C\u662F\u9009\u62E9\u63A8\u5E7F\u7684\u76EE\u6807\u7EC4\u3002\u5982\u679C\u9700\u8981\uFF0C\u60A8\u53EF\u4EE5\u521B\u5EFA\u65B0\u7684\u7279\u5B9A\u201C\u79FB\u52A8\u201D\u7EC4\u3002\u60A8\u53EF\u4EE5\u4E3A\u5C3D\u53EF\u80FD\u591A\u7684\u7528\u6237\u7EC4\u63D0\u4F9B\u79FB\u52A8\u8BBF\u95EE\u6743\u9650\uFF0C\u5E76\u9010\u6B65\u5411\u4E0D\u540C\u7528\u6237\u7EC4\u63A8\u51FA\u79FB\u52A8\u8BBF\u95EE\u6743\u9650\u3002</p><h3 id="\u79FB\u52A8\u7535\u5B50\u90AE\u4EF6\u901A\u77E5-mobile-email-notification" tabindex="-1"><a class="header-anchor" href="#\u79FB\u52A8\u7535\u5B50\u90AE\u4EF6\u901A\u77E5-mobile-email-notification" aria-hidden="true">#</a> \u79FB\u52A8\u7535\u5B50\u90AE\u4EF6\u901A\u77E5 Mobile Email Notification</h3><p><img src="'+p+'" alt="Mobile Settings Mobile Notification"></p><p>\u79FB\u52A8\u7535\u5B50\u90AE\u4EF6\u901A\u77E5(Mobile Email Notification)\u529F\u80FD\u7B80\u5316\u4E86\u79FB\u52A8\u7528\u6237\u6FC0\u6D3B\u6D41\u7A0B\uFF0C\u5E76\u63D0\u9AD8\u4E86\u5458\u5DE5\u5BF9\u65B0\u79FB\u52A8\u529F\u80FD\u7684\u8BA4\u8BC6\uFF0C\u6211\u4EEC\u521B\u5EFA\u4E86\u79FB\u52A8\u7535\u5B50\u90AE\u4EF6\u901A\u77E5\u529F\u80FD\u3002\u6B64\u529F\u80FD\u5141\u8BB8\u60A8\u81EA\u5B9A\u4E49\u7535\u5B50\u90AE\u4EF6\u6A21\u677F\uFF0C\u8BE5\u6A21\u677F\u5C06\u7528\u4E8E\u5411\u5DF2\u6388\u4E88\u79FB\u52A8\u8BBF\u95EE\u6743\u9650\u7684\u6240\u6709\u7528\u6237\u53D1\u9001\u5355\u4E2A\u7535\u5B50\u90AE\u4EF6\u901A\u77E5\u3002</p><p>\u7528\u6237\u6536\u5230\u7535\u5B50\u90AE\u4EF6\u65F6\uFF0C\u4F1A\u770B\u5230\u4E24\u4E2A\u94FE\u63A5\u3002\u4ECE\u5176\u79FB\u52A8\u8BBE\u5907\u5355\u51FB\u7535\u5B50\u90AE\u4EF6\u4E2D\u7684\u94FE\u63A5\u65F6\uFF0C\u7528\u6237\u53EF\u4EE5\u6267\u884C\u4EE5\u4E0B\u529F\u80FD\uFF1A</p><ul><li><p>\u4ECE\u76F8\u5E94\u7684\u5E94\u7528\u5546\u5E97\u4E0B\u8F7D\u5E76\u5B89\u88C5 SAP SuccessFactors \u79FB\u52A8\u5E94\u7528\u7A0B\u5E8F\u3002\u6839\u636E\u68C0\u6D4B\u5230\u7684\u79FB\u52A8\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u6B64\u91CD\u5B9A\u5411\u662F\u81EA\u52A8\u7684\u3002<br> Download and install the SAP SuccessFactors mobile application from the corresponding app store. This redirecting is automatic, based on the mobile operating system that is detected.</p></li><li><p>\u4ECE\u8BBE\u5907\u6FC0\u6D3B\u79FB\u52A8\u5E94\u7528\u3002\u6B64\u6D41\u7A0B\u5229\u7528\u5E38\u89C4 SAP SuccessFactors Web \u5E94\u7528\u7A0B\u5E8F\u767B\u5F55\u673A\u5236\u3002<br> Activate the mobile application from the device. This process leverages the regular SAP SuccessFactors web application login mechanism.</p></li></ul><p>\u9009\u62E9 \u7535\u5B50\u90AE\u4EF6\u901A\u77E5\u6A21\u677F\u8BBE\u7F6E(E-mail Notification Template Settings) \uFF0C\u7136\u540E\u6839\u636E\u9700\u8981\u6253\u5F00\u9875\u9762\u5E95\u90E8\u663E\u793A\u7684\u79FB\u52A8\u6FC0\u6D3B\u901A\u77E5\u7535\u5B50\u90AE\u4EF6\u6A21\u677F\u3002</p><h3 id="\u79FB\u52A8\u8BBE\u5907\u6FC0\u6D3B-mobile-device-activation" tabindex="-1"><a class="header-anchor" href="#\u79FB\u52A8\u8BBE\u5907\u6FC0\u6D3B-mobile-device-activation" aria-hidden="true">#</a> \u79FB\u52A8\u8BBE\u5907\u6FC0\u6D3B Mobile Device Activation</h3><p><img src="'+u+'" alt="Mobile Device Activation"></p><p>\u60A8\u53EF\u4EE5\u901A\u8FC7\u8F6C\u5230 Settings &gt; Mibile \u5E76\u8F93\u5165\u8BBE\u5907\u6FC0\u6D3B\u4EE3\u7801\u6765\u6FC0\u6D3B\u7528\u6237\u7684\u79FB\u52A8\u8BBE\u5907\u3002\u6FC0\u6D3B\u8BBE\u5907\u540E\uFF0C\u5C06\u5217\u51FA\u4E0E\u8BE5\u7528\u6237\u76F8\u5173\u8054\u7684\u8BBE\u5907\u7684\u5217\u8868\uFF0C\u5E76\u5217\u51FA\u505C\u7528\u8BE5\u8BBE\u5907\u7684\u9009\u9879\u3002</p><p>\u9075\u5FAA\u6B64\u4E00\u6B21\u6027\u6D41\u7A0B\uFF0C\u5C06\u60A8\u7684 SAP SuccessFactors Mobile \u5E94\u7528\u4E0E SAP SuccessFactors HXM \u8D26\u6237\u540C\u6B65\u3002\u6309\u7167\u667A\u80FD\u624B\u673A\u4E0A\u7684\u5206\u6B65\u5C4F\u5E55\u751F\u6210\u552F\u4E00\u7684\u6FC0\u6D3B\u7801\u3002</p><p>\u4E0B\u8F7D\u5E94\u7528</p><p>\u901A\u8FC7\u667A\u80FD\u624B\u673A\u8BBF\u95EE\u667A\u80FD\u624B\u673A\u79FB\u52A8\u5E94\u7528\u5546\u5E97\uFF0C\u641C\u7D22\u201CSuccessFactors \u201D\u3002\u6309\u7167\u4E0B\u8F7D\u8BF4\u660E\u8FDB\u884C\u64CD\u4F5C\u3002</p><p>\u8F93\u5165\u6FC0\u6D3B\u7801</p><p>\u4ECE\u60A8\u7684 PC \u767B\u5F55\u60A8\u7684 SAP SuccessFactors \u5E10\u6237\u3002\u9009\u62E9 \u8BBE\u7F6E(Settings) \u83DC\u5355\u3002\u9009\u62E9 \u79FB\u52A8(Mobile) \u3002\u8F93\u5165 \u6FC0\u6D3B\u7801(Activation Code) \u3002</p><h3 id="\u79FB\u52A8\u7528\u6237\u7BA1\u7406-mobile-user-management" tabindex="-1"><a class="header-anchor" href="#\u79FB\u52A8\u7528\u6237\u7BA1\u7406-mobile-user-management" aria-hidden="true">#</a> \u79FB\u52A8\u7528\u6237\u7BA1\u7406 Mobile User Management</h3><p>\u7BA1\u7406\u79FB\u52A8\u7528\u6237(Mobile User Management) \u663E\u793A\u81F3\u5C11\u5DF2\u6FC0\u6D3B\u4E00\u4E2A\u79FB\u52A8\u8BBE\u5907\u7684\u6240\u6709\u7528\u6237\u3002\u6B64\u6E05\u5355\u8FD8\u663E\u793A\u6BCF\u4E2A\u7528\u6237\u5DF2\u6FC0\u6D3B\u7684\u8BBE\u5907\u6570\u3002\u5728\u6B64\u9875\u9762\u4E2D\uFF0C\u5355\u51FB \u5BFC\u51FA\u6240\u6709\u79FB\u52A8\u7528\u6237(Export All Mobile Users) \u6309\u94AE\u4E0B\u8F7D\u4E00\u4E2A .csv \u6587\u4EF6\uFF0C\u5176\u4E2D\u5305\u542B\u79FB\u52A8\u7528\u6237\u7684\u5B8C\u6574\u5217\u8868\u4EE5\u53CA\u6BCF\u4E2A\u79FB\u52A8\u7528\u6237\u5DF2\u6FC0\u6D3B\u7684\u8BBE\u5907\u6570\u91CF\u3002</p><p>\u60A8\u53EF\u4EE5\u901A\u8FC7\u5728 \u4EBA\u5458\u641C\u7D22(People Search) \u5B57\u6BB5\u4E2D\u8F93\u5165\u79FB\u52A8\u7528\u6237\u7684\u540D\u5B57\u548C\u59D3\u6C0F\u6216\u7528\u6237\u540D\u6765\u505C\u7528\u5176\u8BBE\u5907\u3002\u9009\u62E9\u7528\u6237\u540D\u65F6\uFF0C\u53EF\u4EE5\u4F7F\u7528\u4E0E\u8BE5\u7528\u6237\u76F8\u5173\u8054\u7684\u8BBE\u5907\u7684\u5217\u8868\uFF0C\u4EE5\u53CA\u6FC0\u6D3B\u6216\u53D6\u6D88\u6FC0\u6D3B\u8BBE\u5907\u7684\u9009\u9879\u3002</p><p>\u9009\u62E9\u9875\u9762\u5E95\u90E8\u7684 \u53D1\u9001\u65B0\u7684\u79FB\u52A8\u6FC0\u6D3B\u7535\u5B50\u90AE\u4EF6(Send a New Mobile Activation Email) \u4EE5\u5C06\u79FB\u52A8\u6FC0\u6D3B\u7535\u5B50\u90AE\u4EF6\u91CD\u65B0\u53D1\u9001\u7ED9\u7279\u5B9A\u7528\u6237\u3002\u53EA\u6709\u5F53\u60A8\u6FC0\u6D3B \u79FB\u52A8\u6FC0\u6D3B(Mobile Activation) \u529F\u80FD\u65F6\uFF0C\u6B64\u529F\u80FD\u624D\u53EF\u7528\u3002</p><h3 id="\u7EC3\u4E60\u4E5D\u5982\u4F55\u542F\u7528\u79FB\u52A8\u8BBE\u5907\u7528\u6237\u7684\u8BBF\u95EE-how-to-enable-access-for-a-mobile-device-user" tabindex="-1"><a class="header-anchor" href="#\u7EC3\u4E60\u4E5D\u5982\u4F55\u542F\u7528\u79FB\u52A8\u8BBE\u5907\u7528\u6237\u7684\u8BBF\u95EE-how-to-enable-access-for-a-mobile-device-user" aria-hidden="true">#</a> \u{1F389}\u7EC3\u4E60\u4E5D\u5982\u4F55\u542F\u7528\u79FB\u52A8\u8BBE\u5907\u7528\u6237\u7684\u8BBF\u95EE How to Enable Access for a Mobile Device User</h3><p>\u6F14\u793A\u5982\u4F55\u4E3A\u79FB\u52A8\u8BBE\u5907\u7528\u6237\u542F\u7528\u8BBF\u95EE\u3002<br> Demonstrate how to enable access for a mobile device user.</p><ol><li>Enable access for a mobile device user <ol><li>Use the Action Search to navigate to Manage Permission Roles.</li></ol></li><li>Grant permission to the Administrator to access mobile permissions. <ol><li>On the Permission Role List screen, in the Permission Role column, click Administrator .</li><li>On the Permission Role Detail screen, click Permission .</li><li>In the Permission Setting dialog box, in the User Permission list click General User Permission .</li><li>In the General User Permission section, verify that the Mobile Access check-box is selected or activate it if it is not the case.</li><li>Click Done.</li><li>On the Permission Role Detail screen, click Save Changes.</li><li>Logout and login to ensure the new permission is applied correctly.</li><li>From the Action Search go to Enable Mobile Features.</li><li>In the Mobile specific tab, activate the check-boxes for Notifications and Org Chart. Each time click Turn ON and OK.</li><li>For the Modules, activate Goal Management, Career Development Planning and Metric Tiles. Each time, click Turn ON and OK.</li></ol></li></ol>',42);function F(x,N){const i=n("RouterLink");return r(),l(c,null,[g,e("nav",v,[e("ul",null,[e("li",null,[a(i,{to:"#\u79FB\u52A8\u8BBE\u7F6E\u7BA1\u7406-management-of-mobile-settings"},{default:o(()=>[M]),_:1}),e("ul",null,[e("li",null,[a(i,{to:"#\u79FB\u52A8\u529F\u80FD\u542F\u7528-mobile-feature-enablement"},{default:o(()=>[S]),_:1})]),e("li",null,[a(i,{to:"#\u79FB\u52A8\u8BBF\u95EE\u6743\u9650\u7684\u9009\u62E9-selection-of-mobile-access-permissions"},{default:o(()=>[_]),_:1})]),e("li",null,[a(i,{to:"#\u79FB\u52A8\u7535\u5B50\u90AE\u4EF6\u901A\u77E5-mobile-email-notification"},{default:o(()=>[P]),_:1})]),e("li",null,[a(i,{to:"#\u79FB\u52A8\u8BBE\u5907\u6FC0\u6D3B-mobile-device-activation"},{default:o(()=>[A]),_:1})]),e("li",null,[a(i,{to:"#\u79FB\u52A8\u7528\u6237\u7BA1\u7406-mobile-user-management"},{default:o(()=>[y]),_:1})]),e("li",null,[a(i,{to:"#\u7EC3\u4E60\u4E5D\u5982\u4F55\u542F\u7528\u79FB\u52A8\u8BBE\u5907\u7528\u6237\u7684\u8BBF\u95EE-how-to-enable-access-for-a-mobile-device-user"},{default:o(()=>[w]),_:1})])])])])]),E],64)}var k=s(f,[["render",F],["__file","Lesson3.html.vue"]]);export{k as default};
