import{_ as i,r as o,o as n,c as l,a as e,b as s,w as r,F as u,d as t,e as c}from"./app.be1e3df6.js";var d="/THR81/assets/20220509114403.69b1ee2f.png",h="/THR81/assets/20220509114554.b47a2176.png";const p={},f=e("h1",{id:"\u4E1A\u52A1\u89C4\u5219",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u4E1A\u52A1\u89C4\u5219","aria-hidden":"true"},"#"),t(" \u4E1A\u52A1\u89C4\u5219")],-1),g={class:"table-of-contents"},b=t("\u4E1A\u52A1\u89C4\u5219\u6982\u89C8 Business Rules Overview"),m=t("\u89C4\u5219\u4E8B\u4EF6 Rule Events"),v=t("MDF \u63D0\u9192\u4E8B\u4EF6 MDF Alert Event"),y=t("\u786E\u5B9A\u4F7F\u7528\u89C4\u5219\u5F15\u64CE\u914D\u7F6E\u4E1A\u52A1\u89C4\u5219\u7684\u7528\u4F8B Identify Use Cases for Configuring Business Rules Using the Rules Engine"),w=t("\u66F4\u6539\u89C4\u5219\u65B9\u6848\u7684\u5411\u5BFC Wizard to Change the Scenario of Rules"),_=c('<div class="custom-container tip"><p class="custom-container-title">\u672C\u8282\u76EE\u6807</p><ul><li>\u5B9A\u4E49\u4E1A\u52A1\u89C4\u5219</li><li>\u786E\u5B9A\u4F7F\u7528\u89C4\u5219\u5F15\u64CE\u914D\u7F6E\u4E1A\u52A1\u89C4\u5219\u7684\u7528\u4F8B</li></ul></div><h2 id="\u4E1A\u52A1\u89C4\u5219\u6982\u89C8-business-rules-overview" tabindex="-1"><a class="header-anchor" href="#\u4E1A\u52A1\u89C4\u5219\u6982\u89C8-business-rules-overview" aria-hidden="true">#</a> \u4E1A\u52A1\u89C4\u5219\u6982\u89C8 Business Rules Overview</h2><p><img src="'+d+'" alt="Business Rules"></p><p>\u60A8\u53EF\u4EE5\u4F7F\u7528\u89C4\u5219\u5F15\u64CE(Rules Engine)\u4E3A\u5404\u79CD\u6A21\u5757\u548C\u529F\u80FD\u914D\u7F6E\u4E1A\u52A1\u89C4\u5219\u903B\u8F91\u3002</p><p>\u4E1A\u52A1\u89C4\u5219\u53EF\u4EE5\u6DB5\u76D6\u6CD5\u5F8B\u89C4\u5B9A\uFF08\u201C\u5728\u7F8E\u56FD\u5DE5\u4F5C\u7684\u5458\u5DE5\u9700\u8981 FLSA \u72B6\u6001\u201D\uFF09\u3001\u516C\u53F8\u653F\u7B56\uFF08\u201C\u6240\u6709\u8FC1\u5F80\u4F26\u6566\u529E\u4E8B\u5904\u7684\u5458\u5DE5\u90FD\u83B7\u5F97\u9AD8\u751F\u6D3B\u6210\u672C\u7684\u8865\u507F\u201D\u6216\u5176\u4ED6\u8981\u6C42\uFF09\u3002\u7531\u4E8E\u7279\u5B9A\u5BA2\u6237\u9700\u6C42\uFF0C\u89C4\u5219\u53EF\u9AD8\u5EA6\u5B9A\u5236\uFF0C\u5E76\u57FA\u4E8E\u5BA2\u6237\u5148\u524D\u5236\u5B9A\u7684\u51B3\u7B56\u914D\u7F6E\u3002</p><p>\u89C4\u5219\u5F15\u64CE\u63D0\u4F9B\u6613\u4E8E\u4F7F\u7528\u7684\u5DE5\u5177\uFF0C\u7528\u4E8E\u52A8\u6001\u914D\u7F6E\u548C\u7BA1\u7406\u9700\u8981\u5728GO \u4E2D\u51FA\u73B0\u7684\u5BA2\u6237\u3001\u56FD\u5BB6\u6216\u573A\u666F\u7279\u5B9A\u4E1A\u52A1\u903B\u8F91\u3002</p><ul><li><p>\u7528\u6237\u65E0\u9700\u5728\u6280\u672F\u4E0A\u7CBE\u901A\u5373\u53EF\u521B\u5EFA\u4E1A\u52A1\u89C4\u5219\u3002<br> Users do not need to be technically savvy to create business rules.</p></li><li><p>\u76F4\u89C2UI \u5141\u8BB8\u7528\u6237\u914D\u7F6E\u9700\u8981\u68C0\u67E5\u7684\u89C4\u5219\u6761\u4EF6\uFF0C\u4EE5\u89E6\u53D1\u89C4\u5219\u64CD\u4F5C\u3002<br> The intuitive UI allows users to configure rule conditions that need to be checked in order to trigger the rule actions.</p></li><li><p>\u8FD9\u4E9B\u89C4\u5219\u53EF\u4EE5\u8F7B\u677E\u66F4\u65B0\uFF0C\u4EE5\u6EE1\u8DB3\u5BA2\u6237\u4E0D\u65AD\u53D8\u5316\u7684\u4E1A\u52A1\u573A\u666F\u7684\u9700\u6C42\u3002<br> These rules can be updated easily to meet the needs of customer\u2019s ever changing business scenarios.</p></li></ul><p>\u4E1A\u52A1\u89C4\u5219(Business Rules)\u5728\u89C4\u5219\u5F15\u64CE\u4E2D\u5B9A\u4E49\uFF0C\u7CFB\u7EDF\u5728\u8FD0\u884C\u65F6\u6267\u884C\u8FD9\u4E9B\u89C4\u5219\u3002\u4ECE\u6280\u672F\u4E0A\u8BB2\uFF0C\u89C4\u5219\u5F15\u64CE\u57FA\u4E8E\u5143\u6570\u636E\u6846\u67B6(MDF)\uFF0C\u4F46\u4F7F\u7528\u5176\u81EA\u5DF1\u7684\u5728\u201C\u7BA1\u7406\u5DE5\u5177\u201D\u90E8\u5206\u4E2D\u5DE5\u5177\uFF1A\u201C\u914D\u7F6E\u4E1A\u52A1\u89C4\u5219(Configure Business Rules)\u201D\u3002</p><p>\u89C4\u5219\u53EF\u7528\u4E8E\u5C06\u9A8C\u8BC1\u6DFB\u52A0\u5230\u7528\u6237\u8F93\u5165\u7684\u8BE6\u7EC6\u4FE1\u606F\u6216\u81EA\u52A8\u586B\u5145\u67D0\u4E9B\u5B57\u6BB5\u6570\u636E\uFF08\u4F8B\u5982\uFF0C\u65F6\u533A\u4FE1\u606F\u53EF\u57FA\u4E8E\u4F4D\u7F6E\u4FE1\u606F\u81EA\u52A8\u586B\u5145\uFF09\u3002</p><p>\u5B9A\u4E49\u89C4\u5219\u7684\u65B9\u6CD5\u6709\u4E24\u79CD\uFF1A</p><ol><li><p>\u57FA\u672C\u89C4\u5219\uFF08\u4E0D\u63A8\u8350\uFF0C\u56E0\u4E3A\u5B83\u5BB9\u6613\u51FA\u9519\uFF09<br> Basic Rules (Not Recommended as it is error prone)</p></li><li><p>SF \u5B9A\u4E49\u7684\u573A\u666F\uFF08\u63A8\u8350\uFF09<br> SF defined scenarios (Recommended)</p></li></ol><h3 id="\u89C4\u5219\u4E8B\u4EF6-rule-events" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5219\u4E8B\u4EF6-rule-events" aria-hidden="true">#</a> \u89C4\u5219\u4E8B\u4EF6 Rule Events</h3><p>\u4E1A\u52A1\u89C4\u5219\u53EF\u4EE5\u9644\u52A0\u5230\u4E0D\u540C\u7684\u4E8B\u4EF6\u3002\u8FD9\u4E9B\u4E8B\u4EF6\u5305\u62EC\uFF1A</p><ul><li><p><strong>Initialize Rules</strong>\uFF1A\u5728\u6240\u6709\u5176\u4ED6\u89C4\u5219\u4E4B\u524D\u89E6\u53D1\u521D\u59CB\u5316\u89C4\u5219\uFF1B\u6B64\u7C7B\u89C4\u5219\u53EF\u7528\u4E8E\u81EA\u52A8\u586B\u5145\u4E0D\u540C\u5B57\u6BB5\u7684\u7F3A\u7701\u503C\u3002\u5B83\u4EEC\u4EC5\u5728\u521B\u5EFA\u65B0\u8BB0\u5F55\uFF08\u521D\u59CB\u5316\uFF09\u65F6\u8FD0\u884C\u3002<br> Initialization rules are triggered before all other rules; such rules are useful to autofill default values for different fields. They only run when a new record is created (initialized).</p></li><li><p><strong>Validate Rules</strong>\uFF1A\u9A8C\u8BC1\u89C4\u5219\u662F\u5728\u63D0\u4EA4\u5BF9\u8C61\u66F4\u6539\u540E\u4F46\u5728\u4FDD\u5B58\u66F4\u6539\u4E4B\u524D\u89E6\u53D1\u7684\u3002\u60A8\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E9B\u89C4\u5219\u6765\u9A8C\u8BC1\u7528\u6237\u8F93\u5165\u7684\u5B57\u6BB5\u503C\u3002\u8FD9\u4E9B\u64CD\u4F5C\u5728\u4FDD\u5B58\u89C4\u5219\u4E4B\u524D\u6267\u884C\u3002\u4F7F\u7528\u5BFC\u5165\u548C\u5BFC\u51FA\u6570\u636EUI \u5BFC\u5165MDF\u6570\u636E\u65F6\uFF0C\u9A8C\u8BC1\u64CD\u4F5C\u4F1A\u89E6\u53D1\u4E0EMDF\u5BF9\u8C61\u76F8\u5173\u8054\u7684\u9A8C\u8BC1\u89C4\u5219\uFF0C\u5E76\u8FD4\u56DE\u6570\u636E\u4E2D\u53D1\u73B0\u7684\u9519\u8BEF\u3002\u7136\u540E\uFF0C\u7BA1\u7406\u5458\u53EF\u4EE5\u901A\u8FC7\u9A8C\u8BC1\u53D1\u73B0\u4EFB\u4F55\u6570\u636E\u95EE\u9898\u5E76\u5728\u5BFC\u5165\u4E4B\u524D\u5BF9\u5176\u8FDB\u884C\u66F4\u6B63\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u9A8C\u8BC1\u89C4\u5219\u5728\u9A8C\u8BC1\u548C\u5BFC\u5165\u64CD\u4F5C\u671F\u95F4\u6267\u884C\u3002<br> Validate rules are triggered after a change to an object is submitted but before the change is saved. You can use these rules to validate field values entered by user. These are executed before Save Rules. When you import MDF data using the Import and Export Data UI, the validate operation triggers the validate rules associated with the MDF objects and returns errors found in the data. Administrators can then catch any data issues through validation and correct them before importing it. In fact, the validate rules are executed during both validate and import operations.</p></li><li><p><strong>Save Rules</strong>\uFF1A\u4FDD\u5B58\u89C4\u5219\u5728\u7528\u6237\u4FDD\u5B58\u66F4\u6539\u65F6\u89E6\u53D1\u3002\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E9B\u89C4\u5219\u5728\u4FDD\u5B58\u4E4B\u524D\u586B\u5145\u6216\u66F4\u6539\u5B57\u6BB5\u503C\uFF0C\u5E76\u4E14\u57FA\u4E8E\u7528\u6237\u8F93\u5165\u503C\u3002\u4F8B\u5982\uFF0C\u60A8\u5E0C\u671B\u6839\u636E\u56FD\u5BB6/\u5730\u533A\u503C\u9009\u62E9\u81EA\u52A8\u586B\u5145\u5730\u533A\u5B57\u6BB5\u3002<br> Save rules are triggered when a user saves changes. You can use these rules to populate or change field values before saving and they are based on user inputs values. For example, you want to auto populate region field based on country values selection.</p></li><li><p><strong>Post Save Rules</strong>\uFF1A\u4FDD\u5B58\u5BF9\u8C61\u540E\u89E6\u53D1\u4FDD\u5B58\u540E\u89C4\u5219\u3002\u5F53\u60A8\u60F3\u8981\u5411\u7528\u6237\u53D1\u9001\u8B66\u62A5\u6D88\u606F\u65F6\uFF0C\u4F7F\u7528\u8FD9\u4E9B\u89C4\u5219\u3002\u5B83\u4EEC\u4E0D\u7528\u4E8E\u8BBE\u7F6E\u5B57\u6BB5\u503C\u3002<br> Post Save rules are triggered after changes to an object have been saved. These rules are used when you want to send an alert message to the user. They are not used to set a field value.</p></li><li><p><strong>Change Rules (onChange)</strong>\uFF1A\u5728\u5B57\u6BB5\u7EA7\u522B\u4F7F\u7528\u3002\u65E0\u6CD5\u5C06\u5BF9\u8C61\u4E0E\u66F4\u6539\u89C4\u5219\u76F8\u5173\u8054\u3002\u66F4\u6539\u7279\u5B9A\u5B57\u6BB5\u7684\u503C\u65F6\u89E6\u53D1\u66F4\u6539\u89C4\u5219\u3002\u60A8\u53EF\u4EE5\u4F7F\u7528\u6B64\u89C4\u5219\u5728\u66F4\u6539\u540E\u586B\u5145\u5176\u4ED6\u5B57\u6BB5\u3002\u4F8B\u5982\uFF0C\u60A8\u53EF\u4EE5\u6839\u636E\u5BF9\u201C\u4F4D\u7F6E\u201D\u5B57\u6BB5\u7684\u66F4\u6539\uFF0C\u7528\u7279\u5B9A\u503C\u586B\u5145\u201C\u56FD\u5BB6/\u5730\u533A\u201D\u5B57\u6BB5\u3002<br> They are used at the field level. Objects cannot be associated with change rules. Change rules are triggered when a value for a particular field is changed. You can use this rules to populate another field after the change. For example, you can populate the Country field with a certain value based on a change to the Position field.</p></li><li><p><strong>Delete Rules</strong>\uFF1A\u5220\u9664\u5BF9\u8C61\u8BB0\u5F55\u540E\u89E6\u53D1\u5220\u9664\u89C4\u5219\u3002<br> Deletion rules are triggered after an object record is deleted.</p></li><li><p><strong>On Load Rules</strong>\uFF1A\u6B64\u7C7B\u89C4\u5219\u5BF9\u4E8E\u8BA1\u7B97\u7684\u77AC\u6001\u5B57\u6BB5\u975E\u5E38\u6709\u7528\u3002\u8FD9\u662FUI \u7279\u5B9A\u7684\u89C4\u5219\uFF0C\u5728\u52A0\u8F7DUI \u540E\u6267\u884C\u3002\u4F8B\u5982\uFF0C\u6839\u636E\u7ED9\u5B9A\u5BF9\u8C61\u4E0A\u63D0\u4F9B\u7684\u4E0D\u540C\u5B57\u6BB5\u8BA1\u7B97\u5DE5\u8D44\u603B\u989D\u3002<br> Such rules are useful for transient fields which are calculated. This is UI specific rules and gets executed once the UI is loaded. An example could be calculating total salary based on different fields available on given object.</p></li><li><p><strong>UI Rules</strong>\uFF1A \u6B64\u7C7B\u89C4\u5219\u4EC5\u9002\u7528\u4E8E\u53EF\u914D\u7F6E\u7528\u6237\u754C\u9762\u3002\u8FD9\u4E9B\u662F\u6839\u636E\u7279\u5B9A\u6761\u4EF6\u4F7F\u7279\u5B9A\u5B57\u6BB5\u6210\u4E3A\u5FC5\u586B\u3001\u53EF\u89C1\u6216\u9690\u85CF\u7684UI \u7279\u5B9A\u89C4\u5219\u3002\u5F53\u7528\u6237\u6267\u884C\u5BFC\u5165\u65F6\uFF0C\u5C06\u4E0D\u4F1A\u5E94\u7528\u6B64\u7C7B\u89C4\u5219\u3002<br> Such rules are applicable only for Configurable UI. These are UI specific rules to make certain field required, visible or hide based on certain condition. Such rules will not be applied when a user does the import.</p></li></ul><p>\u9A8C\u8BC1\u548C\u4FDD\u5B58\u89C4\u5219\u4E2D\u7684\u8B66\u544A\u6D88\u606F\u663E\u793A\u5728\u5DE5\u4F5C\u6D41\u89C4\u5219\u6267\u884C\u7684\u5DE5\u4F5C\u6D41\u786E\u8BA4\u6D88\u606F\u4E4B\u524D\u3002<br> Warning messages from the validate and save rules are shown before the workflow confirmation message from workflow rule execution.</p><p>\u53EF\u66F4\u6539\u73B0\u6709\u89C4\u5219\u7684\u57FA\u7840\u573A\u666F\u3002\u60A8\u53EF\u4EE5\u5C06\u573A\u666F\u4ECE\u57FA\u672C\u66F4\u6539\u4E3A\u5E94\u7528\u7A0B\u5E8F\u7279\u5B9A\uFF0C\u6216\u4ECE\u4E00\u4E2A\u5E94\u7528\u7A0B\u5E8F\u7279\u5B9A\u7684\u573A\u666F\u66F4\u6539\u4E3A\u53E6\u4E00\u4E2A\u5E94\u7528\u7A0B\u5E8F\u7279\u5B9A\u573A\u666F\u3002 The underlying scenarios of existing rules can be changed. You can change the scenario from basic to application-specific, or from one application-specific scenario to another application-specific scenario.</p><h3 id="mdf-\u63D0\u9192\u4E8B\u4EF6-mdf-alert-event" tabindex="-1"><a class="header-anchor" href="#mdf-\u63D0\u9192\u4E8B\u4EF6-mdf-alert-event" aria-hidden="true">#</a> MDF \u63D0\u9192\u4E8B\u4EF6 MDF Alert Event</h3><p>Alerts \u53EF\u5E2E\u52A9\u60A8\u5728\u4E1A\u52A1\u4E8B\u4EF6\u65F6\u4E3B\u52A8\u901A\u77E5\u5458\u5DE5\u3001\u7ECF\u7406\u6216HR \u7BA1\u7406\u5458\uFF0C\u4EE5\u4FBF\u4ED6\u4EEC\u80FD\u591F\u5728\u6B63\u786E\u7684\u65F6\u95F4\u542F\u52A8\u5FC5\u8981\u7684\u6D3B\u52A8\u3002</p><p>\u4EC5\u5F53\u89C4\u5219\u76EE\u7684\u7C7B\u578B\u4E3AAlert\u65F6\uFF0C\u624D\u80FD\u5728\u57FA\u4E8EMDF\u5BF9\u8C61\u7684\u4E1A\u52A1\u89C4\u5219\u4E2D\u6267\u884C\u89E6\u53D1MDF Alert Event()\u51FD\u6570\u3002</p><p>\u53EA\u6709\u5728\u6279\u51C6\u5DE5\u4F5C\u6D41\u540E\uFF0C\u624D\u4F1A\u4E3A\u5177\u6709\u5DE5\u4F5C\u6D41\u7684MDF\u5BF9\u8C61\u89E6\u53D1\u8B66\u62A5\uFF0C\u800C\u4E0D\u662F\u5728\u6267\u884C\u5DE5\u4F5C\u6D41\u65F6\u89E6\u53D1\u3002</p><h3 id="\u786E\u5B9A\u4F7F\u7528\u89C4\u5219\u5F15\u64CE\u914D\u7F6E\u4E1A\u52A1\u89C4\u5219\u7684\u7528\u4F8B-identify-use-cases-for-configuring-business-rules-using-the-rules-engine" tabindex="-1"><a class="header-anchor" href="#\u786E\u5B9A\u4F7F\u7528\u89C4\u5219\u5F15\u64CE\u914D\u7F6E\u4E1A\u52A1\u89C4\u5219\u7684\u7528\u4F8B-identify-use-cases-for-configuring-business-rules-using-the-rules-engine" aria-hidden="true">#</a> \u786E\u5B9A\u4F7F\u7528\u89C4\u5219\u5F15\u64CE\u914D\u7F6E\u4E1A\u52A1\u89C4\u5219\u7684\u7528\u4F8B Identify Use Cases for Configuring Business Rules Using the Rules Engine</h3><ol><li><p><strong>\u5DE5\u4F5C\u6D41 Workflow</strong>\uFF1A\u60A8\u53EF\u4EE5\u5B9A\u4E49\u89C4\u5219\uFF0C\u5F53\u7ECF\u7406\u6216\u5458\u5DE5\u66F4\u6539\u5458\u5DE5\u6570\u636E\u65F6\uFF0C\u81EA\u52A8\u786E\u5B9A\u6B63\u786E\u7684\u5DE5\u4F5C\u6D41\u3002\u4E3A\u6B64\uFF0C\u521B\u5EFA\u5DE5\u4F5C\u6D41\u57FA\u7840\u5BF9\u8C61\u5E76\u5C06\u5176\u5206\u914D\u5230\u89C4\u5219\u5F15\u64CEUI \u4E2D\u7684\u89C4\u5219\u3002\u4F8B\u5982\uFF0C\u5982\u679C\u5DE5\u8D44\u589E\u957F\u8D85\u8FC7 10%\uFF0C\u5219\u53EF\u4EE5\u89E6\u53D1\u5907\u9009\u5DE5\u4F5C\u6D41\u3002<br> You can define rules that automatically determine the right workflow when the manager or employee changes employee data. To achieve this, you create a workflow foundation object and assign it to the rule in the Rules Engine UI. For example, you can trigger an alternate workflow if the salary increase is over 10%.</p></li><li><p><strong>\u4F20\u64AD Propagation</strong>\uFF1A\u60A8\u53EF\u4EE5\u5B9A\u4E49\u4F20\u64AD\u89C4\u5219\uFF0C\u4EE5\u4F7F\u7CFB\u7EDF\u81EA\u52A8\u5C06\u6570\u636E\u4ECE\u4E00\u4E2A\u5B57\u6BB5\u590D\u5236\u5230\u53E6\u4E00\u4E2A\u5B57\u6BB5\u3002\u8FD9\u6837\uFF0C\u60A8\u53EF\u4EE5\u5728\u7CFB\u7EDF\u7684\u591A\u4E2A\u4F4D\u7F6E\u62E5\u6709\u76F8\u540C\u7684\u6570\u636E\uFF0C\u540C\u65F6\u4EC5\u4FDD\u7559\u4E00\u4E2A\u6570\u636E\u8BB0\u5F55\u3002\u4F8B\u5982\uFF0C\u60A8\u53EF\u4EE5\u5C06\u804C\u4F4D\u4EE3\u7801\u4F20\u64AD\u5230\u804C\u4F4DMDF\u5BF9\u8C61\u3002<br> You can define propagation rules to have the system automatically copy over the data from one field to another field. This way you can have the same data in several places of the system, while keeping just one data record. For example, you can propagate the job code to the Position MDF object.</p></li><li><p><strong>\u8BA1\u7B97 Calculations</strong>\uFF1A\u60A8\u53EF\u4EE5\u4F7F\u7528\u89C4\u5219\u5F15\u64CE\u652F\u6301\u7684\u5404\u79CD\u529F\u80FD\u5B9A\u4E49\u81EA\u52A8\u6267\u884C\u8BA1\u7B97\u7684\u89C4\u5219\u3002\u4F8B\u5982\uFF0C\u60A8\u53EF\u4EE5\u83B7\u53D6\u5458\u5DE5\u7684\u5F53\u524D\u5E74\u9F84\uFF0C\u60A8\u53EF\u4EE5\u8BA1\u7B97\u5F53\u524D\u65E5\u671F\u548C\u5458\u5DE5\u51FA\u751F\u65E5\u671F\u4E4B\u95F4\u7684\u5DEE\u5F02\u3002<br> You can define rules that automatically perform calculations using the various functions the Rules Engine supports. For example, you can get an employee&#39;s current age, you can calculate the difference between the current date and the employee&#39;s birth date.</p></li><li><p><strong>\u9A8C\u8BC1 Validation</strong>\uFF1A\u53EF\u4EE5\u4F7F\u7528\u9A8C\u8BC1\u89C4\u5219\u8BA9\u7CFB\u7EDF\u5728\u4FDD\u5B58\u524D\u68C0\u67E5\u7528\u6237\u7684\u8F93\u5165\u3002\u60A8\u53EF\u4EE5\u5C06\u5B57\u6BB5\u8BBE\u7F6E\u4E3A\u5FC5\u586B\uFF0C\u4E5F\u53EF\u4EE5\u89E6\u53D1\u9519\u8BEF\u6D88\u606F\u3002\u4F8B\u5982\uFF0C IF... \u8BE5\u56FD\u5BB6/ \u5730\u533A\u662FUSA\uFF0CTHEN... \u9700\u8981FLSA \u72B6\u6001\u3002<br> You can use validation rules to let the system check the user&#39;s input before saving. You can set a field to mandatory, or you can trigger error messages. For example, IF... the country is USA, THEN... the FLSA status is required.</p></li><li><p><strong>\u8D44\u683C Eligibility</strong>\uFF1A\u60A8\u53EF\u4EE5\u5B9A\u4E49\u54EA\u4E9B\u5458\u5DE5\u5E94\u5305\u542B\u5728\u5956\u91D1\u8BA1\u5212\u6216\u85AA\u916C\u8BA1\u5212\u8868\u4E2D\u3002\u4E3A\u6B64\uFF0C\u60A8\u5FC5\u987B\u5C06\u201C\u6D6E\u52A8\u5DE5\u8D44\u201D\u6216 \u201C\u85AA\u916C\u201D\u6A21\u5757\u4E0E Employee Central\u96C6\u6210\u3002\u4F8B\u5982\uFF0C\u89C4\u5219\u53EF\u4EE5\u662F\uFF1A<br> You can define which employees should be included in a bonus plan or compensation planning form. To achieve this, you have to integrate the modules Variable Pay or Compensation with Employee Central. For example, the rule could be:<br> IF... \u5458\u5DE5\u7C7B\u578B\u4E0D\u7B49\u4E8E\u201C\u5408\u540C\u201D\u3001\u201C\u4E34\u65F6\u201D\u6216\u201C\u8054\u5408\u201D\u5E76\u4E14\u5458\u5DE5\u662F\u5B9A\u671F\u7684<br> \u5E76\u4E14\u6BD4\u7387\u7C7B\u578B\u4E3A\u201C\u6BCF\u5C0F\u65F6\u201D\u548C\u201C\u6709\u85AA\u201D\u5E76\u4E14\u96C7\u7528\u65E5\u671F\u665A\u4E8E 2010 \u5E74 10 \u6708 1\u65E5\u5E76\u4E14\u91CD\u65B0\u96C7\u7528\u65E5\u671F\u665A\u4E8E 2010 \u5E74 10 \u6708 1\u65E5\u5E76\u4E14\u5458\u5DE5\u72B6\u6001\u4E3A\u6D3B\u52A8\u6216STD<br> THEN... \u6B64\u5458\u5DE5\u5E94\u7B26\u5408\u6761\u4EF6\uFF08\u9488\u5BF9\u7279\u5B9A\u85AA\u916C\u8868\u5355\uFF09<br> IF... the employee type does not equal Contract, Temporary or Union AND the employee is regular<br> AND the rate type is &#39;Hourly&#39; and &#39;Salaried&#39; AND the hire date is after 10/01/2010 AND the rehire date is after 10/01/2010 AND the employee status is Active or STD<br> THEN... this employee should be eligible (for a specific compensation form)</p></li><li><p><strong>\u7F3A\u7701\u503C Defaulting Values</strong>\uFF1A\u60A8\u53EF\u4EE5\u5B9A\u4E49\u7279\u5B9A\u5B57\u6BB5\u7684\u7F3A\u7701\u503C\u3002\u4F8B\u5982\uFF0C\u5982\u679C\u7BA1\u7406\u5458\u4E3A\u516C\u53F8COMP_USA \u6DFB\u52A0\u65B0\u5458\u5DE5\uFF0C\u5219\u8BE5\u5458\u5DE5\u5C06\u81EA\u52A8\u6709\u8D44\u683C\u83B7\u5F97\u5E93\u5B58\uFF0C\u521D\u59CB\u5E93\u5B58\u6388\u4E88\u8BBE\u7F6E\u4E3A 200 \u3002<br> You can define default values for specific fields. For example, if the Admin adds a new employee for the company COMP_USA, the employee is automatically eligible for stock, and the initial stock grant is set to 200.</p></li></ol><h3 id="\u66F4\u6539\u89C4\u5219\u65B9\u6848\u7684\u5411\u5BFC-wizard-to-change-the-scenario-of-rules" tabindex="-1"><a class="header-anchor" href="#\u66F4\u6539\u89C4\u5219\u65B9\u6848\u7684\u5411\u5BFC-wizard-to-change-the-scenario-of-rules" aria-hidden="true">#</a> \u66F4\u6539\u89C4\u5219\u65B9\u6848\u7684\u5411\u5BFC Wizard to Change the Scenario of Rules</h3><p><img src="'+h+'" alt="Change Scenario"></p><p>\u7531\u4E8E\u57FA\u4E8E\u6709\u610F\u4E49\u573A\u666F\u7684\u89C4\u5219\u975E\u5E38\u91CD\u8981\uFF0C\u5C24\u5176\u662F\u4E3A\u4E86\u6700\u5927\u9650\u5EA6\u5730\u51CF\u5C11\u5BA2\u6237\u53EF\u80FD\u9047\u5230\u7684\u914D\u7F6E\u95EE\u9898\u3002</p><p>\u5728\u5F15\u5165\u5E94\u7528\u7A0B\u5E8F\u7279\u5B9A\u573A\u666F\u4E4B\u524D\uFF0C\u4E1A\u52A1\u89C4\u5219\u662F\u4F7F\u7528\u7F3A\u7701\u57FA\u672C\u573A\u666F\u521B\u5EFA(default Basic scenario)\u7684\u3002\u7531\u4E8E\u6B64\u573A\u666F\u4E0D\u63D0\u4F9B\u4EFB\u4F55\u53EF\u7528\u4E8E\u914D\u7F6E\u89C4\u5219\u7684\u5404\u79CD\u5BF9\u8C61\u3001\u53C2\u6570\u548C\u64CD\u4F5C\u7684\u6307\u5357\uFF0C\u56E0\u6B64\u751F\u6210\u7684\u89C4\u5219\u901A\u5E38\u4F1A\u4EA7\u751F\u9519\u8BEF\u3002\u56E0\u6B64\uFF0C\u6211\u4EEC\u5EFA\u8BAE\u60A8\u5C06\u57FA\u4E8E\u57FA\u672C\u573A\u666F\u7684\u73B0\u6709\u89C4\u5219\u6539\u4E3A\u7279\u5B9A\u4E8E\u5E94\u7528\u7A0B\u5E8F\u7684\u89C4\u5219\u573A\u666F\u3002</p><p>\u5C06\u573A\u666F\u4ECE\u57FA\u672C(Basic)\u66F4\u6539\u4E3A\u5E94\u7528\u7A0B\u5E8F\u7279\u5B9A\u573A\u666F\u540E\uFF0C\u65E0\u6CD5\u5C06\u5176\u66F4\u6539\u56DE\u57FA\u672C\u3002\u56E0\u6B64\uFF0C\u5728\u66F4\u6539\u573A\u666F\u4E4B\u524D\uFF0C\u5EFA\u8BAE\u60A8\u59CB\u7EC8\u5148\u5BFC\u51FA\u5F53\u524D\u89C4\u5219\u5B9A\u4E49\u3002\u8FD9\u6837\uFF0C\u5982\u679C\u65B0\u89C4\u5219\u65B9\u6848\u672A\u6309\u8BA1\u5212\u5DE5\u4F5C\uFF0C\u60A8\u5C06\u83B7\u5F97\u4E00\u4E9B\u7ED3\u679C\u3002</p>',27);function F(R,x){const a=o("RouterLink");return n(),l(u,null,[f,e("nav",g,[e("ul",null,[e("li",null,[s(a,{to:"#\u4E1A\u52A1\u89C4\u5219\u6982\u89C8-business-rules-overview"},{default:r(()=>[b]),_:1}),e("ul",null,[e("li",null,[s(a,{to:"#\u89C4\u5219\u4E8B\u4EF6-rule-events"},{default:r(()=>[m]),_:1})]),e("li",null,[s(a,{to:"#mdf-\u63D0\u9192\u4E8B\u4EF6-mdf-alert-event"},{default:r(()=>[v]),_:1})]),e("li",null,[s(a,{to:"#\u786E\u5B9A\u4F7F\u7528\u89C4\u5219\u5F15\u64CE\u914D\u7F6E\u4E1A\u52A1\u89C4\u5219\u7684\u7528\u4F8B-identify-use-cases-for-configuring-business-rules-using-the-rules-engine"},{default:r(()=>[y]),_:1})]),e("li",null,[s(a,{to:"#\u66F4\u6539\u89C4\u5219\u65B9\u6848\u7684\u5411\u5BFC-wizard-to-change-the-scenario-of-rules"},{default:r(()=>[w]),_:1})])])])])]),_],64)}var I=i(p,[["render",F],["__file","Lesson3.html.vue"]]);export{I as default};
