import{_ as n,r as o,o as c,c as l,a as t,b as e,w as a,F as d,d as s,e as p}from"./app.fc75f062.js";var u="/THR81/assets/20220506180619.ffd381fe.png";const S={},h=t("h1",{id:"\u7BA1\u7406\u5458\u57FA\u7840",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#\u7BA1\u7406\u5458\u57FA\u7840","aria-hidden":"true"},"#"),s(" \u7BA1\u7406\u5458\u57FA\u7840")],-1),m={class:"table-of-contents"},f=s("\u5178\u578BSF\u7CFB\u7EDF\u7BA1\u7406\u5458\u7684\u89D2\u8272\u548C\u804C\u8D23 Roles and Responsibilities of a Typical SAP SuccessFactors System Administrator"),_=s("\u7279\u5B9A\u4E8E SAP SuccessFactors \u7684\u901A\u7528\u672F\u8BED Common Terms Specific to SAP SuccessFactors"),A=s("\u767B\u5F55\u95EE\u9898"),b=s("\u91CD\u7F6E\u5BC6\u7801 Reset Passwords"),y=s("\u91CD\u7F6E\u9501\u5B9A\u7684\u7528\u6237\u5E10\u6237 Reset Locked User Accounts"),P=s("\u5355\u70B9\u767B\u5F55(SSO) Single Sign On (SSO)"),g=s("SAP Cloud Identity Authentication \u670D\u52A1 (IAS)"),F=p('<div class="custom-container tip"><p class="custom-container-title">\u672C\u8282\u76EE\u6807</p><ul><li><p>\u8BC6\u522B\u7BA1\u7406\u5458\u4EFB\u52A1</p></li><li><p>\u89E3\u51B3\u7528\u6237\u767B\u5F55\u95EE\u9898</p></li><li><p>\u4ECB\u7ECD SAP Cloud Identity Authentication\u670D\u52A1 (IAS)</p></li></ul></div><h2 id="\u5178\u578Bsf\u7CFB\u7EDF\u7BA1\u7406\u5458\u7684\u89D2\u8272\u548C\u804C\u8D23-roles-and-responsibilities-of-a-typical-sap-successfactors-system-administrator" tabindex="-1"><a class="header-anchor" href="#\u5178\u578Bsf\u7CFB\u7EDF\u7BA1\u7406\u5458\u7684\u89D2\u8272\u548C\u804C\u8D23-roles-and-responsibilities-of-a-typical-sap-successfactors-system-administrator" aria-hidden="true">#</a> \u5178\u578BSF\u7CFB\u7EDF\u7BA1\u7406\u5458\u7684\u89D2\u8272\u548C\u804C\u8D23 Roles and Responsibilities of a Typical SAP SuccessFactors System Administrator</h2><p>\u4E0D\u540C\u7684 SAP SuccessFactors \u7BA1\u7406\u5458\u5177\u6709\u4E0D\u540C\u7684\u89D2\u8272\u548C\u804C\u8D23\u3002</p><p>\u4EE5\u4E0B\u804C\u8D23\u4E0E\u7CFB\u7EDF\u6240\u6709\u8005\u6216\u62E5\u62A4\u8005(System Owner or Advocate)\u76F8\u5173\uFF1A</p><ul><li>\u4F5C\u4E3A\u7CFB\u7EDF\u7528\u6237\u548C\u5176\u4ED6\u5185\u90E8\u7CFB\u7EDF\u4E4B\u95F4\u7684\u4E3B\u8981\u8054\u7CFB\u4EBA\u3002<br> Serves as the primary contact between system users and other internal systems Primary</li><li>\u7528\u6237\u548C\u7CFB\u7EDF\u4E4B\u95F4\u7684\u4E3B\u8981\u8054\u7CFB\u4EBA<br> contact between users and the system</li><li>\u53CA\u65F6\u4E86\u89E3\u7CFB\u7EDF\u529F\u80FD\u7684\u6240\u6709\u66F4\u65B0<br> Stay current with all updates to system functionality</li><li>\u4E0E\u516C\u53F8\u8D1F\u8D23\u4EBA\u6C9F\u901A\uFF0C\u5E76\u5411\u516C\u53F8\u6240\u6709\u8005\u63A8\u8350\u4E1A\u52A1\u6D41\u7A0B<br> Communicate with and recommend business processes to the company owners</li></ul><p>\u4EE5\u4E0B\u804C\u8D23\u4E0E\u7CFB\u7EDF\u7BA1\u7406(System Administration)\u76F8\u5173\uFF1A</p><ul><li>\u7BA1\u7406\u548C\u76D1\u7763\u7528\u6237\u4FE1\u606F\uFF08\u4F8B\u5982\uFF0C\u6570\u636E\u5BFC\u5165\u3001\u5BC6\u7801\u548C\u901A\u77E5\uFF09<br> Management and oversight of user information (for example, data imports, passwords and notifications)</li><li>\u7BA1\u7406\u7528\u6237\u6743\u9650\u548C\u7279\u6743<br> Management of user permissions and privileges</li><li>\u7BA1\u7406\u8868\u5355\u3001\u80FD\u529B\u3001\u89D2\u8272\u548C\u7CFB\u7EDF\u901A\u77E5<br> Management of forms, competencies, roles and system notifications</li></ul><p>\u4EE5\u4E0B\u804C\u8D23\u4E0E\u7528\u6237\u652F\u6301(User Support)\u76F8\u5173\uFF1A</p><ul><li>\u4E3A\u6240\u6709\u4EBA\u529B\u8D44\u6E90 (HR) \u7ECF\u7406\u63D0\u4F9B\u5FC5\u8981\u7684\u534F\u52A9\u548C\u652F\u6301<br> Provision of necessary assistance and support for all Human Resource (HR) managers</li><li>\u5B9E\u65BD\u65B0\u529F\u80FD<br> Implementation of new functionality</li><li>\u57F9\u8BAD\u65B0\u529F\u80FD\u5E76\u4E0E\u7528\u6237\u6C9F\u901A<br> Training and communication to users of new functionality</li><li>\u4E3A\u4EBA\u529B\u8D44\u6E90\u548C\u7BA1\u7406\u7528\u6237\u63D0\u4F9B\u62A5\u544A\u534F\u52A9<br> Provision of reporting assistance to HR and management users Provision of coaching</li><li>\u4E3A\u7ECF\u7406\u548C\u5458\u5DE5\u63D0\u4F9B\u6307\u5BFC\u5E2E\u52A9<br> assistance to managers and employees</li></ul><h3 id="\u7279\u5B9A\u4E8E-sap-successfactors-\u7684\u901A\u7528\u672F\u8BED-common-terms-specific-to-sap-successfactors" tabindex="-1"><a class="header-anchor" href="#\u7279\u5B9A\u4E8E-sap-successfactors-\u7684\u901A\u7528\u672F\u8BED-common-terms-specific-to-sap-successfactors" aria-hidden="true">#</a> \u7279\u5B9A\u4E8E SAP SuccessFactors \u7684\u901A\u7528\u672F\u8BED Common Terms Specific to SAP SuccessFactors</h3><table><thead><tr><th>\u672F\u8BED Terms</th><th>\u5B9A\u4E49 Definition</th></tr></thead><tbody><tr><td>\u4EEA\u8868\u76D8 Dashboards</td><td>\u8FD9\u662F SAP SuccessFactors \u4E2D\u7684\u4E00\u79CD\u62A5\u8868\uFF0C\u5305\u542B\u56FE\u50CF\u3001\u56FE\u8868\u6216 Portlet\u3002</td></tr><tr><td>\u6240\u6709\u5458\u5DE5\u7EC4 All Employees Group</td><td>\u8BE5\u7EC4\u5305\u542B\u5BFC\u5165\u5230 SAP SuccessFactors \u4E14\u6709\u6743\u767B\u5F55\u7684\u6240\u6709\u4E2A\u4EBA\u3002</td></tr><tr><td>\u5458\u5DE5\u6570\u636E\u6587\u4EF6 Employee Data File</td><td>\u6B64\u6587\u4EF6\u5305\u542B\u6240\u6709\u7528\u6237\u8BB0\u5F55\u3002\u6B64\u6587\u4EF6\u4E2D\u5305\u62EC\u521B\u5EFA\u76EE\u6807\u3001\u8BC4\u4F30\u6216\u8BC4\u4F30\u4ED6\u4EBA\u7684\u5458\u5DE5\u3002</td></tr><tr><td>\u8868\u5355\u6A21\u677F Form Templates</td><td>\u8FD9\u4E9B\u6A21\u677F\u5305\u542B\u6BCF\u4E2A\u8868\u5355\u7684\u5E03\u5C40\u3001\u90E8\u5206\u548C\u5DE5\u4F5C\u6D41\u3002\u5B83\u4EEC\u7531 SAP SuccessFactors \u987E\u95EE\u5728\u5B9E\u65BD\u671F\u95F4\u521B\u5EFA\u3002\u5B83\u4EEC\u7528\u4E8E\u4E3A\u7FA4\u4F53\u521B\u5EFA\u5355\u4E2A\u8868\u5355\u3002\u6A21\u677F\u201C\u7C7B\u578B\u201D\u786E\u5B9A\u8868\u5355\u6A21\u677F\u7684\u7528\u9014\u3002</td></tr><tr><td>\u8868\u5355 Forms</td><td>\u8868\u5355\u4E5F\u79F0\u4E3A\u6587\u6863\u3002\u5B83\u4EEC\u4ECE\u8868\u5355\u6A21\u677F\u521B\u5EFA\uFF0C\u7528\u4E8E\u8BB0\u5F55\u4FE1\u606F\uFF0C\u5305\u62EC\u5BA1\u6838\u5468\u671F\u4E2D\u7684\u5458\u5DE5\u7EE9\u6548\u8BC4\u4F30\u3002\u6BCF\u4E2A\u5458\u5DE5\u6536\u5230\u7684\u8868\u5355\u90FD\u5305\u542B\u5176\u6240\u57FA\u4E8E\u7684\u8868\u5355\u6A21\u677F\u7684\u6240\u6709\u5143\u7D20\u3002\u5206\u914D\u8868\u5355\u7684\u6D41\u7A0B\u79F0\u4E3A \u542F\u52A8 \u3002</td></tr><tr><td>\u5B9E\u4F8B Instance</td><td>\u8FD9\u662F SAP SuccessFactors \u670D\u52A1\u5668\u4E0A\u7684\u5BA2\u6237\u7A7A\u95F4\u3002\u6BCF\u4E2A\u5BA2\u6237\u90FD\u4E0E\u4E13\u4E1A\u670D\u52A1\u987E\u95EE\u5171\u540C\u914D\u7F6E\u81EA\u5DF1\u7684\u5B9E\u4F8B\u3002</td></tr><tr><td>\u5DE5\u4F5C\u4EE3\u7801 Job Code</td><td>\u8FD9\u5206\u914D\u7ED9\u6BCF\u4E2A\u5458\u5DE5\uFF0C\u4EE5\u4FBF\u201C\u6620\u5C04\u201D\u5176\u89D2\u8272\u7684\u8D44\u683C\u5230\u5DE5\u4F5C\u4EE3\u7801\u3002</td></tr><tr><td>\u89C6\u7EBF Line of Sight</td><td>\u8FD9\u63CF\u8FF0\u4E86 SAP SuccessFactors \u4E2D\u4E2A\u4EBA\u7684\u62A5\u544A\u53EF\u89C1\u6027\u3002\u4F8B\u5982\uFF0C\u7ECF\u7406\u53EF\u4EE5\u67E5\u770B\u76F4\u63A5\u4E0B\u5C5E\u53CA\u4EE5\u4E0B\u4E0B\u5C5E\u3002</td></tr><tr><td>\u95E8\u6237\u7EC4\u4EF6 Protlets</td><td>Portlet \u4E3A\u7528\u6237\u63D0\u4F9B\u8D44\u6E90\u3001\u56FE\u8868\u6216\u529F\u80FD\u56FE\uFF0C\u5E2E\u52A9\u4ED6\u4EEC\u5B8C\u6210\u6D41\u7A0B\u3002Portlet \u662F\u7528\u4E8E\u63CF\u8FF0\u663E\u793A\u7528\u6237\u6570\u636E\u7684\u56FE\u8868\u6216\u56FE\u50CF\u7684\u672F\u8BED\u3002</td></tr><tr><td>\u8BC4\u5206\u91CF\u8868 Rating Scale</td><td>\u8FD9\u786E\u5B9A\u4E86\u7528\u6237\u80FD\u591F\u5728\u8BC4\u4F30\u671F\u95F4\u9009\u62E9\u7684\u503C\u548C\u503C\u7684\u542B\u4E49\u3002</td></tr><tr><td>\u89D2\u8272\u540D\u79F0 Role Names</td><td>\u8FD9\u4E9B\u4EE3\u7801\u4E5F\u79F0\u4E3A\u4EE3\u7801\u3002\u89D2\u8272\u540D\u79F0\u7528\u4E8E\u591A\u4E2A\u6A21\u5757\u5E76\u63A7\u5236\u5404\u79CD\u6743\u9650\uFF1A<br>\u25CF E - \u5458\u5DE5<br>\u25CF EM - \u5458\u5DE5\u7ECF\u7406<br>\u25CF EH - \u5458\u5DE5\u7684\u4EBA\u529B\u8D44\u6E90\u4EE3\u8868<br>\u5458\u5DE5\u5BFC\u5165\u6587\u4EF6\u4E2D\u63D0\u4F9B\u7684\u6570\u636E\u51B3\u5B9A\u7EC8\u6B62 EM \u6216 EH\u89D2\u8272\u3002\u5982\u679C\u5BA2\u6237\u5728\u5176\u5B9E\u4F8B\u4E2D\u542F\u7528\u4E86\u57FA\u4E8E\u89D2\u8272\u7684\u6743\u9650 (RBP) \uFF0C\u5219\u8FD8\u53EF\u4EE5\u8BBE\u7F6E\u81EA\u5DF1\u7684\u89D2\u8272\u3002</td></tr><tr><td>\u6D41\u8F6C\u6B65\u9AA4 Route Maps</td><td>\u6D41\u8F6C\u6B65\u9AA4\u5EFA\u7ACB\u5DE5\u4F5C\u6D41\u548C\u5458\u5DE5\u5728\u4E1A\u52A1\u6D41\u7A0B\u4E2D\u9075\u5FAA\u7684\u6B65\u9AA4\u3002\u6D41\u8F6C\u6B65\u9AA4\u6307\u5B9A\u8868\u5355\u4ECE\u4E00\u4E2A\u5458\u5DE5\u79FB\u52A8\u5230\u53E6\u4E00\u4E2A\u5458\u5DE5\u7684\u987A\u5E8F\uFF0C\u4EE5\u53CA\u5458\u5DE5\u5728\u6BCF\u4E2A\u6B65\u9AA4\u4E2D\u53EF\u4EE5\u8FDB\u884C\u7684\u64CD\u4F5C\u3002</td></tr></tbody></table><h2 id="\u767B\u5F55\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u767B\u5F55\u95EE\u9898" aria-hidden="true">#</a> \u767B\u5F55\u95EE\u9898</h2><h3 id="\u91CD\u7F6E\u5BC6\u7801-reset-passwords" tabindex="-1"><a class="header-anchor" href="#\u91CD\u7F6E\u5BC6\u7801-reset-passwords" aria-hidden="true">#</a> \u91CD\u7F6E\u5BC6\u7801 Reset Passwords</h3><p>\u6709\u4E09\u79CD\u91CD\u7F6E\u7528\u6237\u5BC6\u7801\u7684\u9009\u9879\uFF1A</p><ol><li>\u91CD\u7F6E\u5355\u4E2A\u7528\u6237\u5BC6\u7801\uFF08\u4F7F\u7528\u63D0\u4F9B\u7684\u5BC6\u7801\uFF09\uFF1A\u5C06\u4E00\u4E2A\u7528\u6237\u7684\u5BC6\u7801\u91CD\u7F6E\u4E3A\u7279\u5B9A\u7684\u65B0\u5BC6\u7801\u3002</li><li>\u91CD\u7F6E\u5355\u4E2A\u7528\u6237\u5BC6\u7801\uFF1A\u5C06\u4E00\u4E2A\u7528\u6237\u7684\u5BC6\u7801\u91CD\u7F6E\u4E3A\u7CFB\u7EDF\u751F\u6210\u7684\u5BC6\u7801\u3002</li><li>\u91CD\u7F6E\u7528\u6237\u7EC4\u5BC6\u7801\uFF1A \u5C06\u7279\u5B9A\u7528\u6237\u7EC4\u7684\u5BC6\u7801\u4E00\u6B21\u91CD\u7F6E\u4E3A\u7CFB\u7EDF\u751F\u6210\u7684\u5BC6\u7801\u3002\u5C06\u663E\u793A\u7EC4\u4E2D\u9009\u62E9\u7684\u7528\u6237\u6570\u3002</li></ol><div class="custom-container warning"><p class="custom-container-title">Note</p><p>\u5728 \u201C\u91CD\u7F6E\u5355\u4E2A\u7528\u6237\u5BC6\u7801\uFF08\u63D0\u4F9B\u5BC6\u7801\uFF09\u201D \u9875\u9762\u548C \u201C\u91CD\u7F6E\u5355\u4E2A\u7528\u6237\u5BC6\u7801\u201D \u9875\u9762\u4E0A\uFF0C \u201C\u767B\u5F55\u65B9\u6CD5\u201D \u5217\u53EF\u7528\u3002<br> \u4E3A\u4E00\u7EC4\u7528\u6237\u91CD\u7F6E\u5BC6\u7801\u65F6\uFF0C\u7BA1\u7406\u5458\u53EF\u4EE5\u67E5\u770B\u53D7\u5F71\u54CD\u7684 PWD \u7528\u6237\u548C SSO \u7528\u6237\u7684\u8BA1\u6570\u3002<br> \u91CD\u7F6E SSO \u7528\u6237\u7684\u5BC6\u7801\u65F6\uFF0C\u6D88\u606F\u6307\u793A\u5BC6\u7801\u4EC5\u5F71\u54CD Basic-Authentication \u548C\u57FA\u4E8E\u4EE4\u724C\u7684 SSO\u3002</p></div><p>\u91CD\u7F6E\u5BC6\u7801\u540E\uFF0C\u7528\u6237\u4F1A\u6536\u5230\u7CFB\u7EDF\u7535\u5B50\u90AE\u4EF6\u901A\u77E5\u3002\u7528\u6237\u767B\u5F55\u7CFB\u7EDF\u65F6\uFF0C\u4F1A\u5F39\u51FA \u5BC6\u7801\u66F4\u6539 \u7A97\u53E3\uFF0C\u8981\u6C42\u7528\u6237\u66F4\u65B0\u5176\u5BC6\u7801\u3002\u5982\u679C\u5DF2\u5728\u5B9E\u4F8B\u4E2D\u542F\u7528\u201C\u5B89\u5168\u95EE\u9898\u201D\uFF0C\u5219\u4F1A\u63D0\u793A\u7528\u6237\u5728\u8F93\u5165\u65B0\u5BC6\u7801\u4E4B\u524D\u9009\u62E9\u5B89\u5168\u95EE\u9898\u3002</p><h3 id="\u91CD\u7F6E\u9501\u5B9A\u7684\u7528\u6237\u5E10\u6237-reset-locked-user-accounts" tabindex="-1"><a class="header-anchor" href="#\u91CD\u7F6E\u9501\u5B9A\u7684\u7528\u6237\u5E10\u6237-reset-locked-user-accounts" aria-hidden="true">#</a> \u91CD\u7F6E\u9501\u5B9A\u7684\u7528\u6237\u5E10\u6237 Reset Locked User Accounts</h3><p>\u4EC5\u5F53\u516C\u53F8\u7684\u5BC6\u7801\u7B56\u7565\u8BBE\u7F6E\u5141\u8BB8\u7528\u6237\u5728\u8D26\u6237\u9501\u5B9A\u524D\u8FDB\u884C\u7279\u5B9A\u6B21\u6570\u7684\u5931\u8D25\u767B\u5F55\u5C1D\u8BD5\u65F6\uFF0C\u91CD\u7F6E\u7528\u6237\u5E10\u6237\u624D\u9002\u7528\u3002</p><p>\u5F53\u7528\u6237\u8D85\u51FA\u5141\u8BB8\u7684\u5931\u8D25\u767B\u5F55\u5C1D\u8BD5\u6B21\u6570\u65F6\uFF0C\u7CFB\u7EDF\u4F1A\u81EA\u52A8\u9501\u5B9A\u7528\u6237\u5E10\u6237\u3002</p><p>\u5E10\u6237\u9501\u5B9A\u540E\uFF0C\u5728\u7BA1\u7406\u5458\u91CD\u7F6E\u5E10\u6237\u4E4B\u524D\uFF0C\u7528\u6237\u65E0\u6CD5\u518D\u6B21\u767B\u5F55\u3002\u91CD\u7F6E\u79D1\u76EE\u540E\uFF0C\u53EA\u80FD\u91CD\u65B0\u6FC0\u6D3B\u79D1\u76EE\u4EE5\u4FBF\u7528\u6237\u53EF\u4EE5\u91CD\u65B0\u767B\u5F55\u3002\u65E0\u9700\u8FDB\u884C\u5176\u4ED6\u66F4\u6539\u3002</p><p>\u7528\u6237\u5E10\u6237\u6545\u969C\u6392\u9664</p><p>\u5F53\u7528\u6237\u8D85\u51FA\u5141\u8BB8\u7684\u5931\u8D25\u767B\u5F55\u5C1D\u8BD5\u6B21\u6570\u65F6\uFF0C\u7CFB\u7EDF\u4F1A\u81EA\u52A8\u9501\u5B9A\u7528\u6237\u5E10\u6237\u3002\u8FD9\u610F\u5473\u7740\u4E00\u65E6\u5E10\u6237\u9501\u5B9A\uFF0C\u7528\u6237\u5C06\u65E0\u6CD5\u518D\u6B21\u767B\u5F55\uFF0C\u76F4\u5230\u7BA1\u7406\u5458\u91CD\u7F6E\u8BE5\u5E10\u6237\u3002\u91CD\u7F6E\u8D26\u6237\u65F6\uFF0C\u53EA\u80FD\u91CD\u65B0\u6FC0\u6D3B\u8D26\u6237\uFF0C\u4EE5\u4FBF\u7528\u6237\u53EF\u4EE5\u91CD\u65B0\u767B\u5F55\uFF1B\u4E0D\u4F1A\u8FDB\u884C\u5176\u4ED6\u66F4\u6539\u3002</p><p>\u4F5C\u4E3A\u7BA1\u7406\u5458\uFF0C\u60A8\u53EF\u4EE5\u8F7B\u677E\u5FEB\u901F\u5730\u91CD\u7F6E\u9501\u5B9A\u7684\u7528\u6237\u5E10\u6237\u3002</p><ul><li>\u5728\u201C\u7BA1\u7406\u5458\u4E2D\u5FC3\u201D\u4E2D\uFF0C\u5355\u51FB \u91CD\u7F6E\u7528\u6237\u5E10\u6237 \u3002</li><li>\u60A8\u5C06\u76F4\u63A5\u8F6C\u5230\u4E00\u4E2A\u9875\u9762\uFF0C\u60A8\u53EF\u4EE5\u5728\u5176\u4E2D\u6309\u90E8\u95E8\u3001\u90E8\u95E8\u3001\u7EC4\u6216\u4F4D\u7F6E\u7B5B\u9009\u5458\u5DE5\uFF0C\u6216\u8005\u53EA\u9700\u8F93\u5165\u4ED6\u4EEC\u7684\u59D3\u540D\u6216\u804C\u4F4D\u4EE3\u7801\u3002\u8F93\u5165\u641C\u7D22\u6761\u4EF6\u5E76\u5355\u51FB \u641C\u7D22\u7528\u6237 \u3002</li><li>\u7CFB\u7EDF\u751F\u6210\u7B26\u5408\u6761\u4EF6\u7684\u7528\u6237\u5217\u8868\u3002\u5DF2\u9501\u5B9A\u7528\u6237\u663E\u793A\u5728\u5217\u8868\u4E0A\uFF0C\u72B6\u6001\u5217\u4E2D\u663E\u793A\u4E3A\u7EA2\u8272 X\u3002</li><li>\u901A\u8FC7\u5355\u51FB\u7528\u6237\u540D\u79F0\u65C1\u8FB9\u7684\u590D\u9009\u6846\uFF0C\u9009\u62E9\u8981\u91CD\u7F6E\u7684\u7528\u6237\u3002</li><li>\u5355\u51FB \u91CD\u7F6E\u6240\u9009\u7528\u6237 \u4EE5\u91CD\u7F6E\u548C\u89E3\u9501\u5176\u7528\u6237\u5E10\u6237\u3002</li></ul><h3 id="\u5355\u70B9\u767B\u5F55-sso-single-sign-on-sso" tabindex="-1"><a class="header-anchor" href="#\u5355\u70B9\u767B\u5F55-sso-single-sign-on-sso" aria-hidden="true">#</a> \u5355\u70B9\u767B\u5F55(SSO) Single Sign On (SSO)</h3><p>\u5355\u70B9\u767B\u5F55 (SSO) \u662F\u591A\u4E2A\u76F8\u5173\u4F46\u72EC\u7ACB\u7684\u8F6F\u4EF6\u7CFB\u7EDF\u7684\u8BBF\u95EE\u63A7\u5236\u5C5E\u6027\u3002\u4F7F\u7528\u6B64\u5C5E\u6027\uFF0C\u7528\u6237\u767B\u5F55\u4E00\u6B21\u5E76\u83B7\u5F97\u5BF9\u6240\u6709\u7CFB\u7EDF\u7684\u8BBF\u95EE\u6743\u9650\uFF0C\u800C\u4E0D\u4F1A\u6536\u5230\u767B\u5F55\u6BCF\u4E2A\u7CFB\u7EDF\u7684\u63D0\u793A\u3002SuccessFactors \u63D0\u4F9B\u4E86\u591A\u4E2A SSO\u9009\u9879\uFF0C\u5141\u8BB8\u7528\u6237\u5728\u4E0D\u8F93\u5165\u5176 SuccessFactors \u7528\u6237\u540D\u548C\u5BC6\u7801\u7684\u60C5\u51B5\u4E0B\u8BBF\u95EE\u5E94\u7528\u7A0B\u5E8F\u3002</p><p>SSO \u6253\u5F00\u65F6\u8BBF\u95EE\u548C\u5B9E\u4F8B</p><p>\u5BA2\u6237\u542F\u7528\u5355\u70B9\u767B\u5F55\u540E\uFF0C\u5C06\u65E0\u6CD5\u518D\u767B\u5F55\u5176\u5B9E\u4F8B\u3002\u4EE5\u4E0B\u6B65\u9AA4\u5141\u8BB8\u60A8\u4F7F\u7528\u8F85\u52A9\u767B\u5F55/\u5B89\u5168\u8BBF\u95EE\u767B\u5F55\u5230 \u5B9E\u4F8B\u3002\u8FD9\u662F\u8BBF\u95EE\u8FD9\u4E9B\u5B9E\u4F8B\u7684\u5EFA\u8BAE\u65B9\u6CD5\u3002\u60A8\u65E0\u9700\u542F\u7528\u90E8\u5206\u7EC4\u7EC7 SSO \u6216\u8981\u6C42\u5BA2\u6237\u901A\u8FC7\u5176 SSO \u95E8\u6237\u767B\u5F55\u3002 \u542F\u7528\u8F85\u52A9\u767B\u5F55\u8BBF\u95EE\uFF08\u7BA1\u7406\u652F\u6301\u8BBF\u95EE\uFF09\u6240\u9700\u7684\u6B65\u9AA4</p><ul><li>\u8FDB\u5165\u60A8\u9700\u8981\u8BBF\u95EE\u7684\u516C\u53F8 ID \u7684\u201C\u914D\u7F6E\u201D\u3002</li><li>\u8F6C\u5230 \u516C\u53F8\u8BBE\u7F6E \u3002</li><li>\u9009\u4E2D \u201C\u542F\u7528\u8F85\u52A9\u767B\u5F55\u529F\u80FD\u201D \u6846\uFF0C\u7136\u540E\u5355\u51FB \u201C\u4FDD\u5B58\u201D \u3002</li></ul><p>\u4EE5\u7BA1\u7406\u5458\u8EAB\u4EFD\u767B\u5F55\u6216\u6307\u5BFC\u5BA2\u6237\u7BA1\u7406\u5458\u5B8C\u6210\u4EE5\u4E0B\u6B65\u9AA4\u4EE5\u542F\u7528\u201C\u7BA1\u7406\u652F\u6301\u8BBF\u95EE\u201D\uFF1A</p><div class="custom-container warning"><p class="custom-container-title">Note</p><p>\u5982\u679C\u60A8\u81EA\u5DF1\u8FDB\u884C\u66F4\u6539\uFF0C\u4ECD\u5E94\u786E\u4FDD\u5BA2\u6237 \u7BA1\u7406\u5458 \u4E86\u89E3\u6D41\u7A0B\u3002\u4ED6\u4EEC\u53EF\u80FD\u5E0C\u671B\u5728\u5C06\u6765\u7981\u7528\u6B64\u8BBF\u95EE\u3002</p></div><p>\u8BBF\u95EE\u8981\u5411\u5176\u6DFB\u52A0 \u201C\u7BA1\u7406\u652F\u6301\u8BBF\u95EE\u201D \u6743\u9650\u7684\u89D2\u8272\u3002</p><ul><li>\u8F6C\u5230\u7BA1\u7406\u5458\u5DE5</li><li>\u8BBE\u7F6E\u7528\u6237\u6743\u9650</li><li>\u7BA1\u7406\u6743\u9650\u89D2\u8272\u5E76\u9009\u62E9\u89D2\u8272</li></ul><div class="custom-container warning"><p class="custom-container-title">Note</p><p>\u6700\u4F73\u5B9E\u8DF5\u662F\u5C06\u6B64\u6743\u9650\u6DFB\u52A0\u5230\u5BA2\u6237\u7684 \u7CFB\u7EDF\u7BA1\u7406\u5458 \u89D2\u8272\u3002</p></div><ol><li>\u9009\u62E9\u6743\u9650\u8BBE\u7F6E</li><li>\u9009\u62E9\u7BA1\u7406\u5458\u6743\u9650</li><li>\u9009\u62E9\u7BA1\u7406\u7528\u6237</li><li>\u9009\u62E9 \u201C\u7BA1\u7406\u201D \u201C\u652F\u6301\u8BBF\u95EE\u201D</li><li>\u5355\u51FB \u5B8C\u6210</li><li>\u4FDD\u5B58\u66F4\u6539</li></ol><p><img src="'+u+'" alt="Manage Support Access"></p><p>\u542F\u7528\u6307\u5B9A\u7528\u6237\u7684\u767B\u5F55\u8BBF\u95EE\u6743\u9650</p><p>\u60A8\u9700\u8981\u542F\u7528\u6307\u5B9A\u7528\u6237\u5E10\u6237\u7684\u767B\u5F55\u8BBF\u95EE\u6743\u9650\u3002\u4E3A\u6B64\uFF0C\u8BF7\u8F6C\u5230 \u7BA1\u7406\u5458\u4E2D\u5FC3 \uFF0C\u5728 \u7BA1\u7406\u652F\u6301\u8BBF\u95EE \u4E2D\u8F93\u5165\uFF0C\u7136\u540E\u8F93\u5165\u7528\u6237\u4FE1\u606F\u548C \u641C\u7D22\u7528\u6237 \u3002\u5355\u51FB\u590D\u9009\u6846\u9009\u62E9\u6743\u9650\u5E76\u51B3\u5B9A\u7528\u6237\u662F\u5426\u5177\u6709\u65E0\u9650\u5236\u6216\u65F6\u95F4\u9650\u5236\u8BBF\u95EE\u6743\u9650\uFF0C\u7136\u540E\u9009\u62E9 \u6388\u4E88\u652F\u6301\u7BA1\u7406\u5458\u8BBF\u95EE\u5E10\u6237\u7684\u6743\u9650 \u3002</p><p>\u5982\u679C\u542F\u7528\uFF0C\u5728\u60A8\u7981\u7528\u8BBF\u95EE\u4E4B\u524D\uFF0C\u652F\u6301\u7BA1\u7406\u5458\u5BF9\u7528\u6237\u5E10\u6237\u5C06\u5177\u6709\u4E0D\u53D7\u9650\u5236\u7684\u767B\u5F55\u8BBF\u95EE\u6743\u9650\u3002\u5BA2\u6237\u53EF\u4EE5\u9075\u5FAA\u76F8\u540C\u7684\u6D41\u7A0B\uFF0C\u4F46\u9009\u62E9 \u7981\u7528\u652F\u6301\u8BBF\u95EE \u4EE5\u62D2\u7EDD\u6211\u4EEC\u8BBF\u95EE\u7528\u6237\u3002</p><p>\u4F7F\u7528\u542F\u7528\u7684\u5E10\u6237\u767B\u5F55 \u5B9E\u4F8B\uFF08\u4EC5\u9650\u987E\u95EE/\u5408\u4F5C\u4F19\u4F34\uFF09</p><p>\u8F85\u52A9\u767B\u5F55\u4E0E\u914D\u7F6E\u5DE5\u5177\u96C6\u6210\u3002\u8F6C\u5230 \u914D\u7F6E \u5DE5\u5177\u5E76\u8BBF\u95EE \u5BA2\u6237\u5B9E\u4F8B\u8BBF\u95EE \u4E0B\u7684\u5BA2\u6237\u5B9E\u4F8B\u3002</p><div class="custom-container warning"><p class="custom-container-title">Note</p><p>\u60A8\u5FC5\u987B\u5177\u6709 \u914D\u7F6E\u8BBF\u95EE\u6743\u9650 \u624D\u80FD\u4F7F\u6B64 \u751F\u6548\u3002\u6CA1\u6709\u7279\u6B8A\u914D\u7F6E\u3002\u5B83\u53EA\u662F\u6B63\u5E38\u914D\u7F6E\u8BBF\u95EE\u7684\u4E00\u90E8\u5206\u3002</p></div><p>\u8B66\u544A\uFF1A\u6B64\u64CD\u4F5C\u53EF\u4ECE\u4EFB\u610F\u6D4F\u89C8\u5668\u8FD0\u884C\u3002\u4E0D\u4EC5\u9650\u4E8E SuccessFactors VPN\u3002\u552F\u4E00\u7684\u4FDD\u62A4\u662F\u914D\u7F6E\u767B\u5F55\u540D\u548C\u5BC6\u7801\u3002\u4F7F\u7528\u6B64\u5DE5\u5177\u65F6\uFF0C\u8BF7\u91C7\u53D6\u9002\u5F53\u7684\u9884\u9632\u63AA\u65BD\u3002</p><h2 id="sap-cloud-identity-authentication-\u670D\u52A1-ias" tabindex="-1"><a class="header-anchor" href="#sap-cloud-identity-authentication-\u670D\u52A1-ias" aria-hidden="true">#</a> SAP Cloud Identity Authentication \u670D\u52A1 (IAS)</h2><p>\u6240\u6709 SAP SuccessFactors \u7CFB\u7EDF\u5747\u53EF\u8BBE\u7F6E\u4E3A\u4F7F\u7528 SAP Cloud Platform Identity Authentication\u670D \u52A1\u3002\u5B83\u7531 SAP \u4E91\u89E3\u51B3\u65B9\u6848\uFF08\u5982 SAP SuccessFactors HXM Suite\uFF09\u4EE5\u53CA SAP Cloud Platform \u5E94\u7528\u7A0B\u5E8F\u548C\u4F01\u4E1A\u9884\u7F6E\u5E94\u7528\u7A0B\u5E8F\u4F7F\u7528\u3002</p><p>IAS \u7684\u4E00\u4E9B\u4F18\u52BF\u5305\u62EC\u8FDE\u63A5\u5230\u5176\u4ED6 SAP \u89E3\u51B3\u65B9\u6848\u4EE5\u53CA\u66F4\u591A\u5B89\u5168\u6027\u7684\u80FD\u529B\u3002</p><p>IAS \u8FD8\u63D0\u4F9B\u4E86\u6BD4\u5F53\u524D SAP SuccessFactors \u90E8\u5206 SSO \u9009\u9879\u66F4\u597D\u7684\u767B\u5F55\u4F53\u9A8C\uFF1A</p><ul><li>\u9996\u6B21\u767B\u5F55\u4E0D\u9700\u8981\u5BC6\u7801\u7279\u5B9A\u7684 URL</li><li>\u5982\u679C\u7528\u6237\u6E05\u9664 cookie\uFF0C\u5219\u9700\u8981\u518D\u6B21\u8F93\u5165\u7535\u5B50\u90AE\u4EF6\u6216\u7528\u6237\u540D</li></ul><p>\u53EF\u6839\u636E\u57FA\u4E8E\u98CE\u9669\u7684\u89C4\u5219\u5C06 IAS \u8BBE\u7F6E\u4E3A\u9700\u8981\u4E0D\u540C\u7684\u767B\u5F55\u65B9\u6CD5\uFF0C\u4F8B\u5982\u5BC6\u7801\u3001\u53CC\u56E0\u7D20\u9A8C\u8BC1\u6216\u793E\u4EA4\u767B\u5F55\u3002</p><p>\u7BA1\u7406\u5458\u80FD\u591F\u4F7F\u7528\u5347\u7EA7\u4E2D\u5FC3\u4E2D\u7684\u529F\u80FD\u4ECE\u65E7\u8EAB\u4EFD\u9A8C\u8BC1\u670D\u52A1\u8FC1\u79FB\u5230 IAS\u3002\u76EE\u524D\uFF0C\u5BA2\u6237\u6CA1\u6709\u4E49\u52A1\u62E5\u6709 IAS\uFF0C\u4F46\u5FC5\u987B\u9075\u5FAA\u5206\u9636\u6BB5\u8FC1\u79FB\u8BA1\u5212\u3002</p>',51),v=s("\u6709\u5173 IAS \u7684\u8BE6\u7EC6\u4FE1\u606F\uFF0C\u8BF7\u53C2\u9605\u5E2E\u52A9\u95E8\u6237\uFF1A("),w={href:"https://help.sap.com/viewer/",target:"_blank",rel:"noopener noreferrer"},R=s("https://help.sap.com/viewer/"),I=s(" 568fdf1f14f14fd089a3cd15194d19cc/latest/en-US)");functi